                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kruchy placek z makiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kruchy-placek-z-makiem.jpg", Time = 60, Number_Person = 1, Steps = 8, Instruction = " Do miski włóż mąkę, cukier, jajka i miękką Kasię. 	 Wszystko wymieszaj mikserem i przełóż na obsypany mąką stół.	Szybko zagnieć ciasto, a następnie rozwałkuj na grubość około 1/2 cm.	Wytnij z ciasta prostokąt wielkości formy i włóż je do niej, lekko ponakłuwaj widelcem.	Masę makową wyłóż z puszki do miski.	Żółtka jajek oddziel od białek.	Białka ubij z cukrem pudrem na sztywną pianę.	Żółtka i kaszę dodaj do maku i wymieszaj mikserem.	Masę makową wymieszaj delikatnie z pianą z białek.0Masę przełóż na ciasto.1Resztę ciasta powtórnie zagnieć, rozwałkuj i pokrój w plasterki.2Plasterki połóż na masie makowej i posmaruj rozkłóconym jajkiem.3Piecz w 180 st. C. przez około 40 min. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka cezar z wedzonym pstragiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-cezar-z-wedzonym-pstragiem.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Sałatę umyj, osusz i porwij na mniejsze kawałki.	 Pstrąga pokrój na kawałki, ser wędzony w paski.	Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem i startym serem.	Sałatę wymieszaj z sosem. Na wierzchu ułóż pstrąga oraz ser. Całość posyp grzankami. Przygotowując sałatkę możesz użyć mix sałat Fit&Easy. Dzięki temu nie musisz kupować kilku rodzajów sałat a tylko jedną, gotową i już umytą mieszankę." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "ciasto orzechowe", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/ciasto-orzechowe-5.jpg", Time = 60, Number_Person = 1, Steps = 4, Instruction = " Do dużej miski wsyp orzechy, tartą bułkę, cukier i 2/3 skórki z pomarańczy, wymieszaj mikserem. 	 Połącz ze stopioną i przestudzoną Kasią, a następnie, cały czas mieszając, kolejno dodawaj po jednym jajku.	Przygotowane ciasto przełóż do wysmarowanej Kasią foremki (najlepsza będzie półokrągła forma na comber, ale może też tu być zwykła keksówka o długości około 25 cm).	Piecz 45 min. w 180 st. C.	Upieczone ciasto udekoruj resztą skórki z pomarańczy. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z czerwona fasola i niebieskim serem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-czerwona-fasola-i-niebieskim-serem.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem oraz ketchupem. 	 Boczek pokrój w kostkę i podsmaż na patelni. Wytopiony tłuszcz usuń.	Pokrusz ser. Skwarki boczku wymieszaj z fasolą z puszki, czosnkiem, serem, grzankami oraz szczypiorem.	Całość dokładnie wymieszaj i dodaj do sosu. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka nicejska z wedzonym lososiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-nicejska-z-wedzonym-lososiem.jpg", Time = 45, Number_Person = 4, Steps = 6, Instruction = " Ziemniaki ugotuj w skórce, w osolonym wrzątku, do momentu, gdy będą miękkie. Odstaw do ostygnięcia. 	 Fasolkę oczyść, ugotuj w osolonej wodzie, wystudź w zimnej wodzie i odstaw. To samo zrób z bobem. Jeśli używasz mrożonego, wystarczy, że go rozmrozisz i obierzesz.	Jajka ugotuj na twardo. Obierz i pokrój w ćwiartki.	Na pomidorach natnij skórkę, następnie sparz wrzątkiem. Przełóż do miski z zimną wodą i obierz. Pokrój w ćwiartki. Ziemniaki i cebulę pokrój w plastry.	Przygotuj sos sałatkowy Knorr według wskazówek na opakowaniu. Dodaj musztardę ziarnistą i ponownie wymieszaj.	Na talerzu układaj liście cykorii, następnie porwane liście sałat. Połóż ćwiartki pomidora, plastry ziemniaków i cebuli. Na wierzchu układaj fasolkę szparagową i bób. Posyp oliwkami.	Sałatkę polej sosem, udekoruj ćwiartkami jajek oraz plastrami wędzonego łososia." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "warzywne leczo", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/warzywne-leczo.jpg", Time = 15, Number_Person = 4, Steps = 4, Instruction = " Warzywa gotuj przez 1 minutę. Po gotowaniu odcedź i zachowaj 10 łyżek wody.	 Na patelni rozpuść tłuszcz i podsmaż chwilę pokrojoną w piórka cebulę. 	Po chwili dodaj paprykę, warzywa i smaż wszystko przez 1 minutę.	Teraz dodaj wodę z gotowania, koncentrat pomidorowy i zawartość opakowania sos do warzyw Knorr.	Wszystko dokładnie wymieszaj i gotuj ok. 2 minuty. Na końcu dopraw oregano. Warzywne leczo możesz podawać samodzielnie, np. z pieczywem lub z gotowanymi ziemniakami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "warzywa po chinsku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/warzywa-po-chinsku-2.jpg", Time = 15, Number_Person = 4, Steps = 4, Instruction = " Warzywa gotuj przez 1 minutę. Po gotowaniu odcedź z wody. 	 Na patelni rozpuść tłuszcz i podsmaż chwilę posiekany czosnek i imbir. 	Po chwili dodajwarzywa i smaż, aż wszystko będzie lekko rumiane. 	Teraz dodaj sos sojowy i zawartość opakowania sos do warzyw Knorr.	Wszystko dokładnie wymieszaj i smaż ok. 2 minuty. Podawaj natychmiast po przygotowaniu, z ryżem.Aby nadać potrawie jeszcze bardziej chińskiego aromatu, możesz skropić ją olejem sezamowym. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "pieczarki nadziewane mozzarella", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/pieczarki-nadziewane-mozzarella.jpg", Time = 30, Number_Person = 2, Steps = 3, Instruction = " Pokrój mozzarellę w drobną kostkę, wymieszaj z rozkruszoną Przyprawą w mini kostkach Smażona Cebula Knorr. 	 Obierz pieczarki, nadziej przyprawionym serem.	Panieruj pieczarki w mące, jajku oraz bułce tartej.	Następnie smaż na głębokim tłuszczu do momentu, kiedy się zarumienią. Podawaj gotowe pieczarki z ketchupem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kapusta faszerowana miesem", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kapusta-faszerowana-miesem481518.jpg", Time = 90, Number_Person = 6, Steps = 5, Instruction = " Farsz: posiekaj wołowinę i słoninę. Dodaj przyprawę Knorr, pieprz, ziele angielskie. Posiekaj drobno cebulę i podsmaż, następnie dodaj do farszu.	 Całość zalej niepełną szklanką wody i wymieszaj.	Kostkę Knorr rozprowadź w 250 mililitrach gorącej wody, tak aby powstał esencjonalny bulion.	Obierz kapustę z wierzchnich liści, w całości ugotuj w osolonym wrzątku. Po wyjęciu rozchyl liście tak, by się nie wyłamały i włóż pomiędzy nie farsz mięsny.	Kapustę przełóż do rondla. Polej 125 mililitrami bulionu, roztopionym masłem i na 30 minut wstaw pod przykryciem do piekarnika. 	Po tym czasie przełóż rondel na kuchenkę, zalej drugą połową bulionu i śmietaną, duś. Co chwilę polewaj kapustę powstałym sosem i odwracaj główkę. Kapusta będzie gotowa, kiedy będzie można łatwo wbić w nią widelec. Kapustę podawaj z przecedzonym sosem, który powstał przy przygotowaniu. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "sernik new york", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/cheesecake-sweden-l896140.jpg", Time = 60, Number_Person = 1, Steps = 8, Instruction = " Rozgrzać piekarnik do 150°C. Okrągłą formę 20 cm z wyjmowanym dnem nasmarować tłuszczem i wyłożyć papierem do pieczenia. 	 Herbatniki drobno pokruszyć w maszynce lub blenderem lub umieścić je w worku foliowym na stole i przy pomocy wałka do ciasta zmiażdżyć na drobne okruchy.	Roztopić Kasię w dużym rondlu i wymieszać z okruchami herbatników.	Równomiernie rozprowadzić mieszaninę na dnie przygotowanej formy i mocno docisnąć grzbietem łyżki.	Zetrzeć skórkę z cytryny do dużej miski. Wycisnąć cytrynę i dodać około 2 łyżeczek soku do skórki w misce.	Połączyć mąkę kukurydzianą z sokiem i skórką z cytryny w misce i wymieszać z serkiem, brązowym cukrem i aromatem waniliowym przy pomocy drewnianej łyżki lub blendera. Dobrze wymieszać.	Oddzielić żółtko od białka 1 jajka (to białko nie będzie użyte). Wymieszać żółtko z masą serową, a następnie po jednym dodawać pozostałe jajka. Dobrze wymieszać.	Wyłożyć masę serową na przygotowany spód w formie. Włożyć do nagrzanego piekarnika i piec przez 50-60 minut.	Wyłączyć piekarnik, uchylić drzwiczki i pozostawić sernik do wystygnięcia w piekarniku. Zimny sernik przykryć folią spożywczą i włożyć do lodówki na dzień lub noc. Następnie wyjąć z formy. Ostrożnie oddzielić spód ciasta od dna formy przy pomocy długiego, cienkiego noża. Posypać cukrem pudrem i pokroić na 8-10 kawałków. Zamiast zwykłych herbatników można użyć herbatników czekoladowych, imbirowych lub o innym smaku." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "letnia salatka z truskawkami i szparagami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/letnia-salatka-z-truskawkami-i-szparagami748541.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Jogurt rozmieszaj z sosem sałatkowym Knorroraz pokrojonymi listkami mięty. 	 Szparagi oczyść i sparz we wrzącej, lekko osolonej wodzie z dodatkiem cukru. Pokrój następnie w 3 cm długości kawałki. Truskawki pokrój na ćwiartki a ogórki w cienkie plastry.	W misce wymieszaj delikatnie sałatę, szparagi, ogórka oraz truskawki. Sałatę rozłóż na talerze i polej przygotowanym dressingiem. Udekoruj listkami mięty i podawaj z chrupiącym pieczywem.Aby urozmaicić sałatkę dodaj np. kiełki słonecznika." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "filety sledziowe po kaszubsku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/filety-sledziowe-po-kaszubsku.jpg", Time = 60, Number_Person = 6, Steps = 4, Instruction = " Zagotuj 1 litra wody z octem, liściem laurowym i zielem angielskim. Namocz w zalewie śledzie przez 30 minut. 	 Rozpuść pół kostki rosołowej Rosół z kury z pietruszką i lubczykiem Knorr w szklance wrzątku. Pokrój cebulę i zalej ją wywarem. Dodaj majeranek , pieprz oraz sól.	Duś do momentu, gdy cebula będzie miękka. Dopraw cukrem i solą (jeśli uważasz, że jest to konieczne). Jeśli zachodzi taka potrzeba, dodaj koncentrat pomidorowy.	Nałóż farsz na śledzie, po czym zroluj je i zepnij. Wlej do reszty sosu cebulowego trochę zalewy.	Ułóż roladki w naczyniu, polej sosem cebulowym i odstaw na parę godzin do lodówki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "placek z wisniami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/placek-serowy-z-wisniami.jpg", Time = 60, Number_Person = 1, Steps = 6, Instruction = " Wiśnie odsącz, posyp 2 łyżkami cukru. 	 Kasię utrzyj z resztą cukru, cukrem waniliowym i solą na puszysta masę.	Dodaj całe jaja, żółtka i ser.	Mąkę przesiej z proszkiem do pieczenia i dodaj do masy.	Blachę dokładnie nasmaruj Kasią, rozprowadź na niej ciasto i posyp wiśniami.	Ciasto piecz przez 45 minut w temperaturze 175 stopni.	Po upieczeniu ciasto posyp cukrem pudrem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "sledzie po kujawsku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/sledzie-po-kujawsku.jpg", Time = 30, Number_Person = 4, Steps = 4, Instruction = " Umyte śledzie zalej zimną wodą, zostaw na noc.	 Następnego dnia obierz ze skóry i ości, zalej mlekiem na 2 godziny. 	Jabłka obierz, usuń gniazda, pokrój w plasterki i wrzuć do rondelka na rozgrzany tłuszcz. 	Dodaj przyprawy, wymieszaj, podduś wszystko, ostudź. 	Śledzie pokrojone w cząstki układaj na półmisku, przykryj dekoracyjnie musem jabłkowym, a następnie folią i wstaw na kilka godzin do lodówki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "karp na sposob wschodni", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/karp-na-sposob-wschodni.jpg", Time = 60, Number_Person = 6, Steps = 3, Instruction = " Oczyść karpia, opłucz i pokrój w kawałki. Pokrój drobno cebule, marchew w plastry.	 Zagotuj litr wody z małą ilością soku z cytryny, cebulą, marchwią, liściem laurowym, goździkami, rodzynkami oraz przyprawą Knorr.	Włóż pokrojonego karpia i gotuj około 20 minut, gdy będzie miękki, dodaj zmielone pierniki i gotuj jeszcze 5 minut.	Kawałki karpia ułóż na półmisku, polej sosem, w którym się gotował i odstaw do ostygnięcia. Podawaj na zimno. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "duet miesa i ryby", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/duet-miesa-i-ryby.jpg", Time = 15, Number_Person = 4, Steps = 4, Instruction = " Rybę pokrój na mniejsze kawałki, 8 sztuk. 	 Każdy kawałek skrop sokiem z cytryny i oliwą. 	Posyp pieprzem, listkami oregano i przyprawą Knorr. 	Rybę zwijaj w plastry szynki. 	Przygotowaną porcje smaż po kilka minut z każdej strony, aby ryba pozostała soczysta. Podawaj na przykład z gotowaną fasolką." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zupa fasolowo pomidorowa", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zupa-fasolowo-pomidorowa.jpg", Time = 60, Number_Person = 4, Steps = 6, Instruction = " Zagotuj 1 litr wody, dodaj kostkę Rosołu z kury Knorr i ugotuj wywar. 	 Namocz w wodzie fasolę, następnie ugotuj w części bulionu.	Przesiej mąkę, dodaj jajko i odrobinę wody, zagnieć twarde ciasto. 	Zetrzyj ciasto na tarce o dużych oczkach i dodaj do fasoli pod koniec gotowania. Gotuj do momentu, aż będzie miękkie.	Pomidory sparz i obierz, a następnie pokrój w plastry. Obierz cebulę i razem z papryką pokrój w kostkę.	Boczek pokrój w kostkę i częściowo stop na patelni. Dodaj cebulę i paprykę, smaż wszystko razem. Oprósz mąką, zasmaż i gotuj 10 minut.	Pozostały bulion dodaj do ugotowanej fasoli, następnie wrzućpodsmażone warzywa. Posyp majerankiem, dopraw do smaku i podawaj z pokrojonym w ćwiartki jajkiem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "muffinki ze szpinakiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/muffinki_z_serem_feta_i_szpinakiem5549876.jpg", Time = 30, Number_Person = 10, Steps = 4, Instruction = " Ser feta pokrój w kosteczkę, szpinak rozmroź i odciśnij z wody.	 Boczek i cebulę pokrój w drobną kosteczkę. Podsmaż na patelni, gdy wytopi się tłuszcz dodaj cebulę. Wszystko razem smaż aż cebula się zeszkli. Dodaj szpinak, dopraw go delikatem warzywnym, gałką i ostrą papryką. Wystudź.	Pomieszaj mąkę z proszkiem do pieczenia. Do farszu dodaj majonez i wymieszaj. 	Jaja rozdziel. Ubij białka na sztywno. Białko i żółtka połącz z mąką i mlekiem, dodaj farsz ze szpinakiem i ser feta, wszystko razem delikatnie wymieszaj.	Oliwą wysmaruj foremki do mufinek, oprósz je mąką. Do foremek nałóż ciasto. Całość wstaw do nagrzanego do 180*C piekarnika na około 20 minut. Ser feta możesz zastąpić innym dojrzałym serem, nawet pleśniowym." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "biszkopt z galaretka rabarbarowa", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/biszkopt-z-galaretka-rabarbarowa.jpg", Time = 30, Number_Person = 1, Steps = 2, Instruction = " Żółtka oddzielić od białek. Białka razem z cukrem ubić na sztywną pianę, następnie dodawać po jednym żółtku, cały czas ubijając. 	 Na koniec dodać przesianą mąkę wymieszaną z proszkiem do pieczenia. Wszystko delikatnie wymieszać i wylać na dużą blachę (wysmarowaną Kasią i wysypaną bułką tartą).	Piec około 30 minut w temperaturze 180 stopni Celsjusza. Upieczone ciasto ostudzić i przekroić na dwa placki. Rabarbar rozgotować w szklance wody i na koniec dodać galaretki. Wszystko dokładnie wymieszać. Tężejącą masą przełożyć biszkopt. Można posypać cukrem pudrem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "befsztyk z warzywami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/befsztyk-z-warzywami.jpg", Time = 45, Number_Person = 4, Steps = 5, Instruction = " Polędwicę pokrój w plastry o grubości ok. 3 cm i natnij tak, aby powstała kieszonka. Oprósz pieprzem. 	 Cukinię i bakłażana przekrój wzdłuż na 4 części i pozbądź się nasion, a następnie pokrój w plastry. Cebulę i paprykę pokrój w grubą kostkę.	Warzywa przesmaż na patelni, dodając 1 łyżkę oregano i szczyptę pieprzu. Smaż, aż będą półtwarde, następnie połóż na papierowych ręcznikach, aby odsączyć nadmiar tłuszczu.	Ziemniaki umyj, nie obieraj, pokrój w księżyce. Dodaj paprykę, 1 łyżkę oregano, skrop olejem. Piecz w piekarniku rozgrzanym do 180 °C około 20 min.	Befsztyki nafaszeruj warzywami. Smaż na dobrze rozgrzanym tłuszczu około 6 min. z każdej strony.	Sos Knorr wsyp do wody i zagotuj, dodaj natkę. Polej befsztyki sosem i podawaj z pieczonymi ziemniakami posypanymi posiekaną natką pietruszki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "warianka", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/warianka.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Kwas z kapusty zagotuj z litrem wody i bulionetką Knorr.	 W garnku wymieszaj mąkę z 2 szklankami wody i wlej do gotującego się kwasu z wodą.	Dodaj liść laurowy, kminek i czosnek. Gotuj przez 10 - 15 minut.	Przed podaniem dodaj olej lniany lub skwarki. Podawaj na gorąco z ziemniakami gotowanymi w mundurkach. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "watrobka drobiowa z jablkiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/watrobka-drobiowa-z-jablkiem.jpg", Time = 30, Number_Person = 4, Steps = 5, Instruction = " Jabłka umyj, zostaw nieobrane, usuń gniazda nasienne i pokrój na osiem części. Skrop sokiem z cytryny.	 Tak przygotowane jabłka podsmaż kilka minut na tłuszczu, dodaj majeraneki odstaw.	Na pozostałym tłuszczu podsmaż cebule pokrojoną w talarki na złotawy kolor.	Wątróbkę opłucz, oczyść, wysusz i oprósz lekko mąką oraz przyprawą Knorr. 	Usmaż na rozgrzanym tłuszczu z każdej strony. 	Do wątróbki przełóż cebulę z jabłkami i podsmaż jeszcze chwilę. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "muffinki jablkowe", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/streusel_muffins35893469.jpg", Time = 15, Number_Person = 1, Steps = 4, Instruction = " Piekarnik nagrzej do 200°C. Formę do muffinek natłuść lub wyłóż papierowymi foremkami. Jabłka obierz ze skóry, pokrój w ćwiartki i wytnij gniazda nasienne. Jedno jabłko pokrój na 12 cząstek, pozostałe w małe kawałki. 	 100 g Kasi w temperaturze pokojowej ubij mikserem na gładką masę ze 100 g cukru. Dodaj jajko i dokładnie zmiksuj.	200 g mąki wymieszaj z kakao oraz solą, następnie dodaj wraz z mlekiem do ubitej masy i wymieszaj. Dodaj do ciasta kawałeczki jabłka i wymieszaj.	Przygotuj kruszonkę: 75 g cukru wymieszaj ze szczyptą soli, 125 g mąki oraz orzechami. 75 g Kasi rozpuść w rondelku, dodaj do mąki i wymieszaj widelcem – powstałą kruszonkę odstaw.	Masę na muffinki wyłóż do foremek. Na wierzchu każdej z nich nałóż porcję kruszonki. Tak przygotowane muffinki wstaw do nagrzanego piekarnika i piecz przez 20 minut. Aby upewnić się, czy babeczki są już upieczone, wykonaj próbę suchego patyczka. Upieczone muffinki pozostaw do wystygnięcia. Papierowe foremki do muffinek możesz również zrobić sam w domu: w tym celu papier do pieczenia potnij na 13-14 cm kwadraty, formę do pieczenia muffinek natłuść Kasią a następnie w każde zagłębienie wciśnij wycięty kawałek pergaminu. Przygotowane foremki napełnij ciastem i upiecz." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "mufinki dla diabetykow", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/mufinki-dla-diabetykow.jpg", Time = 15, Number_Person = 1, Steps = 5, Instruction = " Żółtka oddziel od białek i utrzyj z cukrem brzozowym i solą. 	 Kasię rozpuść i wystudź. Do masy żółtkowej dodaj serek i Kasię, wymieszaj.	Następnie dodaj otręby wymieszane z proszkiem do pieczenia.	Białka ubij na sztywną pianę, przełóż do masy i delikatnie wymieszaj łyżką.	Na koniec do masy dodaj pozbawione szypułek porzeczki i wymieszaj.	Masą wypełnij formę do mufinek wyłożoną papierowymi papilotkami.Piecz około 30 minut w 180 st.C " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "muffinki marchewkowe", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/mufinki-marchewkowe-2-036-medium53432.jpg", Time = 15, Number_Person = 1, Steps = 5, Instruction = " W misce wymieszaj mąkę, cukier, proszek do pieczenia, sodę, sól i cynamon.	 Marchew obierz i zetrzyj na tarce o średnich oczkach.	Kasię rozpuść w rondelku. Jajka roztrzep widelcem i wymieszaj z przestudzoną Kasią i startą marchewką. 	Do sypkich składników dodaj masę marchewkową i wymieszaj łyżką.	Ciasto przełóż do formy mufinkowej wyłożonej papierowymi papilotkami i piecz w 180 st.Cok. 25 minut. Funkcja piekarnika góra-dół.	Mufinki wyjmij z pieca i wystudź. W tym czasie utrzyj cukier puder z odrobiną przegotowanej wody na gładki lukier. Wystudzone mufinki posmaruj lukrem i posyp pistacjami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "ciastka z kawalkami czekolady", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/ciastka-zebra-z-kawalkami-czekolady.jpg", Time = 15, Number_Person = 1, Steps = 6, Instruction = " Ciastka: Rozgrzać piekarnik do 190°C. Nasmarować kilka blach tłuszczem i/lub wyłożyć je papierem do pieczenia. 	 Wymieszać mąkę, sodę, proszek do pieczenia i sól w średniej misce. Odłożyć na bok.	W osobnej misce miksować przez ok. 5 minut margarynę Kasię z jednym i drugim cukrem przy pomocy miksera lub drewnianej łyżki, aż do uzyskania lekkiej i puszystej masy. Dodać jajko i aromat waniliowy. Co jakiś czas zbierać masę ze ścianek miski. Stopniowo dodawać mąkę z dodatkami, aż do połączenia w jednolitą masę. Wymieszać z kawałkami ciemnej i białej czekolady.	Nakładać ciasto na blachy łyżką stołową lub łyżeczką do lodów. Zachować między ciastkami odległość ok. 5 cm, aby mogły urosnąć.	Piec przez 10 minut lub do uzyskania złotego koloru. Po wyjęciu pozostawić blachy na 2 minuty na drucianych podstawkach, a następnie zdjąć ciastka i zostawić do wystygnięcia.	Polewa Zebra (opcjonalnie): Kawałki czekolady roztopić w 2 osobnych naczyniach w kuchence mikrofalowej lub na małym ogniu. Oblewać ciastka w paski stopioną ciemną czekoladą na przemian z jasną. Pozostawić do zastygnięcia przed podaniem.	Porada: Oprócz kawałków czekolady, ciastka te smakują także doskonale z orzechami, suszonymi owocami lub ulubionymi cukierkami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "tortilla ogrodnika", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/tortilla-ogrodnika.jpg", Time = 60, Number_Person = 3, Steps = 3, Instruction = " Obierz, opłucz, osusz ziemniaki i pokrój w kostkę. Następnie usmaż na oleju. 	 Paprykę i cukinię pokrój na ćwiartki, usuń nasiona. Cebulę obierz i pokrój w centymetrową kostkę, tak samo jak paprykę i cukinię. Czosnek przeciśnij przez praskę. Całość dodaj na patelnię i smaż jeszcze przez kilka minut	Jaja wymieszaj z Przyprawą Uniwersalną Delikat Knorr. 	Warzywa i ziemniaki mieszaj, a następnie zalej jajkami i smaż do ścięcia. Tortillę podawaj posypaną posiekaną natką pietruszki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "strudel z serem i brokulami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/broccoli-and-cheese-strudel_1407515.jpg", Time = 5, Number_Person = 5, Steps = 4, Instruction = " Brokuł pokrój w drobne różyczki. Cebulę i czosnek drobno posiekaj , zeszklij je w garnku na rozgrzanej oliwie. 	 Do garnka dodaj śmietanę, gotuj pod przykryciem około 3 minut, aż brokuły zrobią się miękkie. Dodaj produkt do zapiekanek, całość dokładnie wymieszaj, gotuj jeszcze kolejne 2 minuty- sos powinien być bardzo gęsty. Garnek z brokułami odstaw na bok do przestygnięcia.	Do brokułów dodaj jedno jajko i ser, całość dopraw do smaku świeżo zmielonym pieprzem. Wszystko razem dokładnie wymieszaj.	Na desce wyłóż trzy płaty ciasta do strudla, przesmaruj każdy płat z wierzchu oliwą, ułóż jeden na drugim. W środek wyłóż farsz z brokułów i sera. Brzegi ciasta zawiń do środka, następnie całość zroluj sztywno, starając się aby farsz nie wydostał się na zewnątrz.	Gotowego strudla nakłuj wykałaczką, umieść na tacy do pieczenia wyłożonej pergaminem. Piecz w nagrzanym do 200*C piekarniku, około z 25-30 minut do uzyskania złotego koloru.Aby uzyskać ostrzejszy smak, zastąp ementaler serem z niebieską pleśnią. Resztki brokułów możesz wykorzystać gotując kremową zupę. Upewnij się, że ser, którego używasz, zawiera podpuszczkę mikrobiologiczną, a nie naturalną." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "miesna zupa meksykanska", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/miesna-zupa-meksykanska.jpg", Time = 45, Number_Person = 2, Steps = 3, Instruction = " Wymieszaj dokładnie mielone mięso z oliwą oraz szczyptą każdej z przypraw, po czym uformuj malutkie kulki (około 1 centymetra średnicy). 	 Pokrój skórkę z pomarańczy w paseczki, sparz na sitku gorącą wodą.	Wymieszaj dokładnie Węgierską zupę gulaszową Knorr z 500 mililitrami gorącej wody.	Dodaj do zupy mięsne kuleczki, skórkę i wyciśnięty sok z połówki pomarańczy. Całość gotuj przez około 15 minut, co jakiś czas mieszając. Na koniec dopraw do smaku, jeśli uznasz, że jest to konieczne. Podawaj zupę na talerzu udekorowanym dookoła przyprawami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "bogracz", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/bogracz282240.jpg", Time = 60, Number_Person = 4, Steps = 3, Instruction = " Bogacz, czyli potrawa gotowana powoli w garnku nad ognieniem. W domowych warunkach możesz użyć po prostu rondla. Pokrój w drobną kostkę słoninę i cebule. Roztop wędzoną słoninę. Dodaj cebule, jak się zeszkli dodaj mieloną paprykę i kminek. Podsmaż chwilę, ale nie pozwól, aby przyprawy się przypaliły. 	 Pokrój wołowinę w kostkę o szerokości około 1,5 centymetra, dodaj do cebuli i duś wszystko przez kilka minut. Wlej 2 litry wody, dodajEsencję do duszonych mięsKnorr oraz pokrojone w mniejsze kawałki świeże pomidory.	Marchewkę, pietruszkę, kolorowe papryki oraz ziemniaki pokrój w kostkę, dodaj do potrawy. Duś całość, do momentu, kiedy mięso będzie miękkie.	Całość duś, do momentu, kiedy cała potrawa nabierze konsystencji gęstej zupy. Bogracz dopraw do smaku szczyptą ostrej papryki. Podawaj z pieczywem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "ciasteczka z orzechami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/ciasteczka-brazowo-biale.jpg", Time = 15, Number_Person = 1, Steps = 6, Instruction = " Miękką Kasię utrzyj z cukrem. 	 Mąkę wymieszaj z mielonymi orzechami i proszkiem do pieczenia, dodaj cynamon, goździki i jajko.	Wszystko wymieszaj mikserem, a następnie zagnieć rękoma na elastyczne ciasto.	Ciasto rozwałkuj na grubość 0,5 cm, a następnie filiżanką wycinaj okrągłe ciasteczka wielkości około 8 cm.	Układaj je na blaszce wyłożonej papierem do pieczenia i piecz w temperaturze 180 stopni C około 15 minut.	Polewę rozpuść w kąpieli wodnej.	Upieczone i ostudzone ciasteczka do połowy maczaj w polewie czekoladowej i dekoruj połówkami orzechów. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "migdalowe ciasto z rabarbarem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/migdalowe-ciasto-z-rabarbarem73881482.jpg", Time = 25, Number_Person = 4, Steps = 5, Instruction = " Wszystkie składniki ciasta siekamy nożem na stolnicy i zagniatamy gładkie ciasto. Zawijamy w folię i odkładamy na 30 minut do lodówki.	 Schłodzone ciasto rozwałkowujemy i wylepiamy nim dno wyłożonej papierem do pieczenia formy o wymiarach 20x20 cm. Nakłuwamy ciasto widelcem i wstawiamy do nagrzanego do 180°C piekarnika na 15 minut.	Rabarbar myjemy, osuszamy papierowym ręcznikiem i kroimy na kawałki o długości około 2 cm.	Na podpieczony spód wykładamy kawałki rabarbaru i lekko oprószamy mąką ziemniaczaną.	Białka ubijamy na sztywno. Następnie dodajemy stopniowo cukier i cały czas miksujemy, aż masa będzie gęsta i lśniąca. Pod koniec miksowania dodajemy mąkę ziemniaczaną.	Masę bezową wykładamy na rabarbar. Pieczemy w temp. 180°C przez ok. 15-20 min do lekkiego zarumienienia. Następnie wyłączamy piekarnik i zostawiamy w nim ciasto jeszcze na 25 minut. Wyjmujemy z pieca i zostawiamy do wystygnięcia. Przed podaniem posypujemy uprażonymi na suchej patelni migdałami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "boeuf bourguignon", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/boeuf-bourguignon.jpg", Time = 90, Number_Person = 4, Steps = 2, Instruction = " Wołowinę, boczek oraz cebulę pokrój w kostkę o grubości 2 cm. Czosnek posiekaj a marchewkę pokrój w półplastry. 	 W garnku przesmaż mięso, dodaj boczek, marchewkę, czosnek oraz koncentrat pomidorowy i smaż chwilę razem. Pieczarki pokrój w cząstki, przesmaż na rozgrzanej patelni i wsyp do garnka. Następnie wlej wino, dodaj ziele angielskie i liść laurowy. Poczekaj, aż wino odparuje o połowę. Dodaj gałązkę tymianku.	Do garnka wlej 600 ml wody i aby wydobyć pełny smak dania dodaj Esencję do duszony mięs Knorr. Garnek przykryj i duś do momentu, gdy mięso będzie miękkie. Gotową potrawę podawaj np. z kaszą. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "wloskie panini z szynka i serem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/wloskie-panini-z-szynka-i-serem.jpg", Time = 30, Number_Person = 4, Steps = 5, Instruction = " Oliwę wlej do kielicha miksera. Dodaj również suszone pomidory, bazylię i kostkę Knorr. Całość zmiksuj na gęstą pastę. 	 Chleb przekrój wzdłuż na pół. Obie części posmaruj pastą z kostki Knorr.	Pomidora oraz oliwki pokrój w plasterki. 	Na kromkę pieczywa połóż dwa plastry sera, plaster szynki, pomidora oraz oliwki. Przykryj drugą kromką. 	Gotowe panini połóż na grillu, przyłóż czymś ciężkim (na przykład garnkiem z wodą) i grilluj z obu stron, aby chleb zrobił się chrupiący i ser wewnątrz dobrze się rozpuścił.	Rukolę wymieszaj z oliwą. Gotowe panini przekrój na pół i podawaj z sałatką." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zeberka bbq", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/amerykanskie-zeberka-bbq.jpg", Time = 90, Number_Person = 4, Steps = 4, Instruction = " Umyj żeberka, oczyść i podziel na mniejsze porcje, na przykład po trzy kostki. 	 Zagotuj około 2 litry wody z kostką Knorr.	Gotuj żeberka w bulionie około 20 minut. Odstaw do ostygnięcia.	Pokrój cebule i czosnek w drobną kostkę i podsmaż w dużym garnku. Dodaj cukier i poczekaj do momentu, kiedy lekko się z karmelizuje. Ziarna kolendry rozdrobnij w moździerzu i wrzuć do garnka razem z przyprawą Knorr oraz pozostałymi przyprawami. Następnie dodaj ketchup i duś przez około 15 minut. Pod koniec sos dopraw miodem. Jeśli będzie zbyt gęsty, dodaj trochę bulionu, który pozostał z gotowania żeberek.W tak przygotowanym sosie marynuj żeberka co najmniej jeden dzień.	Opiekaj mięso na średnio rozgrzanym grillu około 30 minut lub piecz w rozgrzanym do 160°C piekarniku przez około 1 godzinę. Podawaj z kolbami kukurydzy i pieczonymi ziemniakami." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "faszerowane pieczarki z grilla", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/faszerowane-pieczarki-z-grilla.jpg", Time = 60, Number_Person = 4, Steps = 5, Instruction = " Umyj pieczarki, oderwij nóżki, a następnie posiekaj je. Posiekaj również cebulę. 	 Pokrój boczek oraz szynkę w drobną kostkę i przesmaż je na oleju.	Następnie dodaj cebulę i nóżki pieczarek. Smaż je razem przez kilka minut, po czym wymieszaj z bułką tartą i oliwą.	Posiekaj natkę oraz czosnek i dodaj je do farszu, a całość dopraw rozkruszoną przyprawą w mini kostkach Knorr oraz tymiankiem.	Napełnij farszem kapelusze pieczarek i ułóż je na grillu (najlepiej na specjalnej tacy z folii aluminiowej).	Skrop je sokiem z cytryny i opiekaj około 10 minut. Następnie przykryj folią i piecz jeszcze 3-4 minuty.Aby pieczarki zachowały swój biały kolor, skrop je sokiem z cytryny lub opłucz w wodzie z octem.Gotowe pieczarki podawaj z sałatą i pomidorami skropionymi sosem winegret." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "skrzydelka kurze buffalo", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/skrzydelka-kurze-buffalo.jpg", Time = 60, Number_Person = 8, Steps = 4, Instruction = " Selera pokrój w słupki i włóż do wody z lodem na 30 minut.	 Ser pleśniowy pokrusz i dodaj do niego majonez, jogurt oraz przeciśnięty czosnek. Wszystko wymieszaj, aby uzyskać niezbyt gładki sos. 	Ze skrzydełek odetnij końcówki, a następnie przekrój je na połowy wzdłuż stawu. Posyp obficie przyprawą Knorr.	Na rozgrzany olej wrzucaj skrzydełka i smaż je przez 5-8 minut, po czym wyjmij i odsącz. Następnie dopiecz w piekarniku w 180 stopniach przez 20 minut.	Po wyjęciu z piekarniku skrop skrzydełka rozpuszczonym masłem, sosem chilli i octem. Podawaj z selerem i sosem serowym. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zapiekanka po rzymsku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zapiekanka-po-rzymsku.jpg", Time = 45, Number_Person = 4, Steps = 5, Instruction = " Ugotuj makaron w dużej ilościwody. Wyjmij, przelej zimną wodą, odcedź i odsącz. 	 Pokrój boczek w kostkę, zrumień na patelni, skwarki wyjmij.	Sparz pomidory, usuń skórkę i pokrój w kostkę.	Przygotuj Fix Spaghetti Bolognese Knorr zgodnie z instrukcją na opakowaniu. Wymieszaj gorący makaron ze skwarkami, połową sera i pomidorami.	Dodaj przygotowany sos. Dopraw papryką i pieprzem. 	Włóż potrawę do żaroodpornego naczynia wysmarowanego tłuszczem i wysypanego bułką tartą. Posyp pozostałym serem i polej tłuszczem z boczku. Piecz w gorącym piekarniku przez 20 minut. Zapiekankę rzymską podawaj posypaną natką pietruszki oraz surówką. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "golabki", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/golabki.jpg", Time = 90, Number_Person = 8, Steps = 6, Instruction = " Kapustę obierz z pierwszych liści, wytnij głąb i sparz we wrzącej wodzie, następnie obierz z liści. Z każdego liścia zetnij gruby nerw.	 Ryż dodaj do mielonego mięsa razem z jajkami i podsmażoną wcześniej cebulą pokrojoną w kostkę.	Fix Knorr wymieszaj w wodzie zgodnie z instrukcją na opakowaniu i dodaj do mięsa. 	Farsz dokładnie wymieszaj i porcjami nakładaj na liście kapusty. Liście szczelnie zawijaj.	Przygotowane gołąbki układaj w naczyniu żaroodpornym i zalej bulionem. Dodaj zmiażdżony czosnek, liść laurowy i ziele angielskie. 	Przykryte naczynie wstaw do piekarnika nagrzanego do 170°C na około 1,5 godziny. Po tym czasie wyjmij gołąbki. 	Do wywaru dodaj koncentrat i całość zagęść mąką wymieszaną w zimnej wodzie. Zagotuj. Do sosu przełóż gołąbki i chwilę duś. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "azjatycka zupa warzywna", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/azjatycka-zupa-warzywna.jpg", Time = 30, Number_Person = 4, Steps = 4, Instruction = " Grzyby namocz wcześniej. Jak zmiękną pokrój w cienkie paski. Podobnie zrób z makaronem. Zalej go wrzątkiem, a jak zmięknie odcedź go.	 W garnku zagotuj litr wody z kostką Knorr. 	W drugim garnku podsmaż kurczaka pokrojonego w kostkę. 	Następnie dodaj kolorowe papryki pokrojone podobnie do kurczaka. 	Całość zalej gorącym wywarem. Dodaj sos sojowy, sos rybny, grzyby, zielony groszek i kapustę pekińską pokrojoną w kostkę oraz posiekany szczypiorek. Całość zagotuj i natychmiast podawaj z makaronem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "makaron ze szparagami i wedzonym lososiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/makaron-ze-szparagami-i-wedzonym-lososiem.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Zagotuj litr wody i sparz oczyszczone wcześniej szparagi, a następnie w tej samej wodzie ugotuj makaron na sposób al dente. 	 Szparagi pokrój w plastry, cebule pokrój w kostkę. 	Na patelni podsmaż na oliwie szalotki, dodaj kapary, szparagi, sok z cytryny, musztardę i dwa pomidory pokrojone wcześniej w kostkę.	Całość gotuj. Następnie dodaj ser mascarpone i dopraw sos mini kostkami Knorr. Teraz dodaj łososia oraz makaron. Nie gotuj już sosu tylko delikatnie wymieszaj. Natychmiast podawaj." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "makaron z brokulami i migdalami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/makaron-z-brokulami-i-migdalami.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Z brokuła wytnij małe różyczki, następnie sparz krótko w osolonej wrzącej wodzie. Wystudź, wodę pozostaw. 	 Makaron ugotuj al dente w wywarze po gotowaniu brokułów. Odcedź i skrop oliwą.	Zawartość opakowania Fix Knorr wymieszaj w rondelku z 300 mililitrami wody i doprowadź do wrzenia. Gotuj kilka minut. Płatki migdałowe zrumień na rozgrzanej patelni. Pomidorki pokrój na połówki. 	Gorący makaron wymieszaj z sosem, brokułami i pomidorkami, posyp parmezanem i płatkami migdałów." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka makaronowa z brokulami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-makaronowa-z-brokulami.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Makaron ugotuj według wskazówek na opakowaniu. Następnie odcedź go i odstaw. 	 Brokuł podziel na malutkie różyczki i również ugotuj do miękkości, odcedź i schłódź w wodzie z lodem. Dzięki temu pozostaną zielone i jędrne.	Do kielicha miksera włóż rozkruszony ser, wlej oliwę, pół szklanki gorącej wody oraz sos sałatkowy Knorr. Całość dokładnie zmiksuj, aż powstanie gładki sos.	W dużej misce połącz brokuły, makaron, odcedzone kapary oraz sos. Dokładnie wymieszaj. Sałatkę podawaj posypaną posiekaną natką pietruszki i tartym parmezanem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kalafior zapiekany z miesem mielonym", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/mieso-mielone-zapiekane-z-kalafiorem.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Kalafior podgotuj, mięso dopraw przyprawą Knorr i podsmaż. 	 Przygotuj sos serowy: śmietankę wymieszanej z jajkami, startym serem oraz odrobiną soli i pieprzu.	Posmaruj naczynie żaroodporne masłem. Następnie ułóż w nim mięso i kalafiora.	Całość zalej przygotowanym sosem serowym i zapiekaj około 30 minut w piekarniku nagrzanym do 200 stopni.Podawaj gotowe danie z ciabattą oraz sałatą." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "aromatyczny kuskus z warzywami na ostro", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/aromatyczny-kuskus-z-warzywami-na-ostro.jpg", Time = 45, Number_Person = 4, Steps = 4, Instruction = " Na rozgrzany tłuszcz wrzuć skrojoną w małą kostkę cebulę oraz posiekany ząbek czosnku. Kiedy cebula się przyrumieni dodaj kiełbasę pokrojoną w kostkę.	 Smaż dopóki nie zmiękną, a potem dodaj do kiełbasy i wymieszaj. Następnie dodaj pomidory oraz Esencję do duszonych warzyw Knorr i duś przez chwilę mieszając.	Następnie wsyp przyprawy, wyciśnięty ząbek czosnku i duś chwilę, ewentualnie podlewając wodą. Serwuj z przygotowaną wcześniej kaszą kuskus. 	Rozgrzej 2 łyżki oleju w garnku i wrzuć do niego mrożonki. 	Przygotowanie kaszy kuskus. W dwóch takich samym naczyniach odmierz tę samą objętość kaszy i bulionu. Bulion zagotuj, zalej nim kaszę i zakryj. Po 2-3 minutach odkryj kaszę i dobrze wszystko wymieszaj." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "pizza z papryka i serem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/pizza-z-papryka-i-serem.jpg", Time = 90, Number_Person = 4, Steps = 3, Instruction = " W ciepłej wodzie rozpuszczamy cukier i drożdże. Do miski wsypujemy mąkę, sól i wlewamy oliwę, oraz drożdże, które zaczęły rosnąć. Zagniatamy elastyczne ciasto. Zostawiamy do wyrośnięcia.	 Kiedy ciasto podwoi swoja objętość, rozwałkowujemy je i wykładamy nim blaszkę do pizzy. 	Sosem smarujemy wierzch pizzy, układamy plasterki kiełbasy i papryke pokrojona w piórka.	Pizzę zapiekamy ok 10 min w 200 stopniach, posypujemy startym serem i zapiekamy jeszcze 5-7 min." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "lazania z miesem mielonym i szpinakiem", Rate = 0, Level = "Trudne", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/lazania-z-miesem-mielonym-i-szpinakiem.jpg", Time = 60, Number_Person = 4, Steps = 3, Instruction = " Posiekaj drobno cebulę i czosnek, usmaż połowę na rozgrzanym oleju (1 łyżka), dodajprzecier pomidorowy, mięsołyżeczkę cukru i oregano. Całość smaż aż mięso zrobi się zupełnie suche, dodaj wtedy wymieszany w szklance wody produkt Knorr Naturalnie smaczne - Lasagne. Wszystko razem gotuj jeszcze 3- 4 minuty. 	 Ser żółty podziel na dwie części: 50 gram zetrzyj na tarce, 150 gram pokrój w drobnąkostkę.	Na patelni rozgrzej olej, usmaż pozostałą cebulą z czosnkiem. Dodaj rozmrożony i odsączony szpinak, pieprz i gałkę muszkatołową, wymieszaj.	Przygotuj prostokątną formę do pieczenia, posmaruj ją masłem, na spodzie ułóż 8 płaty lazani, następnie połowę mięsnego farszu, czynność powtórz. Wierzch polej śmietaną, posyp startym serem. Piecz 40 minut w temperaturze 180 °C. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "blyskawiczne sledzie po szlachecku", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/blyskawiczne-sledzie-po-szlachecku-w-majonezie.jpg", Time = 15, Number_Person = 3, Steps = 3, Instruction = " Śledzie pokrój na niewielkie kawałki. 	 Grzyby i ogórki odsącz z zalewy i drobno posiekaj, podobnie jak cebulę i jajka ugotowane na twardo. 	Wszystkie składniki wymieszaj z majonezem i posiekaną zieleniną. Dopraw do smaku.	Wyłóż na półmisek i przybierz gałązkami zieleniny. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "cielecina a la wiedenski sznycel", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/cielecina-ala-wiedenski-sznycel.jpg", Time = 45, Number_Person = 4, Steps = 5, Instruction = " Cielęcinę pokrój na mniejsze kawałki (po 80 gramów) w poprzek włókien. Każdy kawałek rozbij tłuczkiem na cienki plaster.	 Kostkę rosołową Knorr rozgnieć w dłoniach i rozetrzyj z bułką tartą tak, aby powstała mieszanka do panierowania. 	Kotleciki cielęce panieruj w mące, roztrzepanym jajku oraz przygotowanej panierce. Od razu smaż na średnio rozgrzanym tłuszczu. 	Kiedy kotleciki nabiorą złoto-brązowej barwy, przełóż je na drugą stronę. Dodaj łyżkę masła i smaż, pamiętając, by tłuszcz oblewał całe mięso (panierka zrobi się delikatniejsza). Po usmażeniu przełóż kotleciki na ręcznik papierowy. 	Ziemniaki ugotuj w całości, a następnie jeszcze ciepłe pokrój w plasterki. 	W misce wymieszaj olej z cukrem, cebulą pokrojoną w piórka, octem i musztardą. Dodaj ciepłe ziemniaki, dokładnie wymieszaj i dopraw szczyptą soli. Wiedeński sznycel podawaj z sałatką ziemniaczaną i cząstką cytryny. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "poledwiczki wieprzowe w ananasie", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/poledwiczki-wieprzowe-w-ananasie.jpg", Time = 60, Number_Person = 4, Steps = 5, Instruction = " Polędwiczki umyj i oczyść z włókien. Przyprawę do mięs Knorr wymieszaj z oliwą i obłóż nią mięso.	 Polędwiczki owiń plastrami wędzonego boczku i zepnij wykałaczkami. Następnie wstaw do lodówki na 30 minut.	Cebulę i kolendrę posiekaj. Ananasa obierz, wytnij twardy środek, pokrój w kostkę. 	Rozgrzej olej na patelni. Obsmaż zamarynowane polędwiczki z każdej strony, po czym ułóż je w naczyniu żaroodpornym. Wstaw do nagrzanego do 170 stopni piekarnika na około 25 minut.	Na tej samej patelni zeszklij na maśle cebulę. Dodaj ananasa i chwilę podduś. Całość zalej szklanką wody, dopraw pieprzem i odrobiną soli (jeśli uznasz to zakonieczne) i dodaj miód. Gotuj do momentu, kiedy ananas będzie całkiem miękki.	Upieczoną polędwiczkę pokrój w plastry i posyp kolendrą. Podawaj z ananasem oraz ryżem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "tort czekoladowy z kremem kakaowym", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/tort-czekoladowy-z-kremem-kakaowym.jpg", Time = 60, Number_Person = 1, Steps = 5, Instruction = " Rozgrzej piekarnik do 200°CRoztop Kasię na patelni, ostudź. Przygotuj dwie miski. W pierwszej wymieszaj mąkę z kakao. W drugiej ubij pianę z białek ze szczyptą soli, następnie stopniowo, nie przerywając ubijania, dosyp cukier, pojedynczo żółtka i po łyżce mąki, wymieszanej z kakao. Na koniec wlej roztopioną Kasię. 	 Masę biszkoptową podziel na trzy części i osobno upiecz biszkopty w tortownicy o średnicy ok. 24 cm wyłożonej papierem do pieczenia. Formę wstaw do piekarnika rozgrzanego do 200°C i piecz ok. 12 minut. Każdy biszkopt ostrożnie wyjmij z formy i ostudź.	W rondelku na niewielkim ogniu rozpuść cukier w wodzie. Zwiększ ogień i zagotuj, ale nie karmelizuj - syrop nie może zacząć ciemnieć (brązowieć). Zdejmij go z ognia i lekko przestudź. W misce zacznij ubijać żółtka, a następnie cienkim strumieniem, nie przerywając ubijania, zacznij dolewać przygotowany wcześniej syrop. Odstaw do całkowitego ostygnięcia.	Ponownie zacznij ubijać masę, porcjami dodawaj miękką Kasię, a na koniec kakao. Całość ubijaj ok. 5 minut.	Biszkopty przełóż gotowym kremem, lekko dociśnij warstwy. Usuń nożem nadmiar kremu wystający po brzegach. Wstaw do lodówki do schłodzenia na ok. 1 godzinę.	Połam czekoladę na kawałki i przełóż do żaroodpornej miski, dodaj Kasię, a następnie ustaw miskę na niewielkim garnku z wrzącą wodą dobranym tak, aby do niego nie wpadła. To ważne - dno miski nie może się stykać z powierzchnią wody. Taki sposób gotowania nazywa się kąpielą wodną. Mieszaj czekoladę i Kasię od czasu do czasu i gdy tylko się roztopią, rozprowadź polewę po torcie i udekoruj posypką. Odstaw do zastygnięcia, przechowuj w lodówce 2-3 dni. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "smazone poledwiczki z sosem grzybowym", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/smazone-poledwiczki-z-sosem-grzybowym-video.jpg", Time = 30, Number_Person = 4, Steps = 6, Instruction = " Grzyby umyj, osusz, pokrój w ćwiartki, cebule pokrój w drobną kostkę, czosnek posiekaj drobniutko 	 Mięso pokrój w plastry po 125 gram, oprósz solą i pieprzem, obsmaż na patelni po dwie minuty z każdej strony, – jeśli mięso wyda ci się jeszcze surowe dosmaż je o kolejną minutę, po tym czasie mięso przełóż na bok. 	Na patelni zeszklij cebulę i czosnek, dodaj grzyby, smaż chwilę mieszając od czasu do czasu, na patelni nie powinno być żadnego soku.	Całość podlej brandy i podpal alkohol. Podgrzewaj cały czas, aż zupełnie odparuje.	Dodaj wtedy łyżkę mąki i dokładnie wymieszaj z grzybami. Grzyby podlej szklanką wody, dodaj śmietanę, wszystko razem gotuj przez pięć minut mieszając co jakiś czas, aż sos zgęstnieje.	Gotowy sos dopraw do smaku kostką Bulionu grzybowego Knorr. 	Mięso na powrót umieść w sosie, podgrzej chwilę, podawaj polane sosem." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "piernik ze sliwkami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/11592075.jpg", Time = 60, Number_Person = 1, Steps = 2, Instruction = " Kasię rozpuść i odstaw do wystudzenia. Jaja utrzyj z cukrem pudrem na puch. 	 Mąkę, sodę, kakao oraz przyprawę do pierników wymieszaj. Utarte jaja połącz z Kasią, mlekiem i mąką z wymieszanymi składnikami.	Dokładnie wymieszaj na jednolitą masę .Na końcu dodaj powidło oraz bakalie i delikatnie wymieszaj " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zasmazana kapusta z grzybami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zasmazana-kapusta-z-grzybami.jpg", Time = 60, Number_Person = 8, Steps = 3, Instruction = " W garnku na oleju podsmaż kiełbasę, cebule i grzyby, dodaj kapustę, czosnek, wszystko zalej wywarem ugotowanym wcześniej. 	 Dodaj kostkę Knorr, wszystko razem gotuj około 30 minut, aż kapusta będzie miękka. 	Na patelni rozpuść masło, połącz je z mąką, dodaj do kapusty cały czas mieszając.	Kapustę gotuj jeszcze około 20 minut, dopraw ją do smaku czarnym pieprzem, koperkiem i sokiem z cytryny." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "tort jablkowy", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/tort-jablkowy.jpg", Time = 15, Number_Person = 1, Steps = 8, Instruction = " Białka ubijamy na sztywną pianę, dodajemy po łyżce cukru i żółtka na przemian. 	 Dosypujemy mąkę z proszkiem i delikatnie mieszamy.	Ciasto przekładamy do dużej, wysmarowanej Kasią i wysypanej bułką tartą tortownicy.	Pieczemy w nagrzanym piekarniku w temperaturze 170 stopni C.	Jabłka obieramy i ścieramy na tarce.	Skrapiamy sokiem z cytryny.	Miękką Kasię ucieramy.	Jajka ubijamy z 4 łyżkami cukru, stawiając miskę do garnka z gotującą wodą.	Gdy masa będzie puszysta, zestawiamy z pary i ubijamy nadal, aż do ostygnięcia.0Ucierając Kasię, dodajemy stopniowo po 2-3 łyżki masy jajecznej.1Dodajemy do niej dobrze wyciśnięte jabłka. Ozdobić. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "udka z kurczaka w sosie", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/udka-z-kurczaka-w-sosie.jpg", Time = 90, Number_Person = 6, Steps = 3, Instruction = " Udka kurczaka umyj i osusz ręcznikiem papierowym. Zdejmij skórkę, skrop sokiem z cytryny i dobrze dopraw mięso przyprawą Knorr oraz posiekaną papryczką chilli. Odstaw na około godzinę do lodówki.	 W tym czasie na patelnię wlej oliwę. Pokrój cebulę w plastry i dodaj ją na patelnię razem z czosnkiem. Podsmażaj, aż wszystko się zeszkli. Następnie dodaj kurczaka i smaż, aż zrobi się rumiany. 	Dodaj sos sojowy i rosół. Duś kurczaka pod przykryciem, aż będzie miękki, około 30 minut.	Gdy mięso będzie już gotowe wyjmij je z sosu. Sos gotuj, aż zgęstnieje. Kurczaka podawaj z sosem i, na przykład z ryżem." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "pomidory faszerowane", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/pomidory-faszerowane.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Zetnij wierzch pomidorów, zachowując kapelusz razem z zieloną szypułką. Pomidory wydrąż, zachowując równieżmiąższ.	 Mięso mielone wymieszaj ze startą na tarce cebulą, namoczoną i odciśniętą bułką, przyprawą do mięs i ugotowanym wcześniej ryżem. Farsz umieść w wydrążonych pomidorach, przykryj je kapeluszami. Pomidory umieść w żaroodpornym naczyniu. 	Resztki wydrążonych pomidorów zmiksuj z Ketchupem Hellmann’s Pikantnym, kostką bulionową i chlebem tostowym. Powstały sos wlej do naczynia.	Danie piecz przez około 30 minut w nagrzanym do 190 °C piekarniku. Podawaj ze śmietaną." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "wesole indyki wielkanocne", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/10988142.jpg", Time = 60, Number_Person = 8, Steps = 6, Instruction = " Ubij Kasię z cukrem na puszystą masę. Stopniowo dodawaj jajka, wciąż ubijając. 	 Dodaj powoli mąkę z proszkiem do pieczenia i wanilią i mieszaj, aż ciasto się połączy.	Dodaj posiekaną czekoladę i delikatnie wymieszaj łyżką.	Włóż papilotki do formy na muffiny i nałóż do nich ciasto do 3 wysokości. Piecz przez 20 minut w temperaturze 180 °C. Wyjmij babeczki z piekarnika i pozostaw do ostygnięcia.	Rozpuść Kasię w rondelku i dodaj ciemną czekoladę. Mieszaj do połączenia składników.	Kiedy mieszanka wystygnie i osiągnie pożądaną konsystencję kremu, udekoruj nim babeczki, dodając M&M's lub Lentilky tak, by stworzyły indyczy ogon.Potnij żelki w małe trójkąty.Uformuj główki indyków.Przyklejaj części do siebie za pomocą rozpuszczonej czekolady. Z lentilków zrób oczka. Szczegóły zrób za pomocą czekolady. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "rozowa zupa buraczkowa z pajeczyna", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/rozowa-zupa-buraczkowa-z-pajeczyna.jpg", Time = 90, Number_Person = 4, Steps = 4, Instruction = " Buraki umyj, zawiń w folię i upiecz w 160 stopniach przez 1 godzinę.	 Po tym czasie ostudź je lekko, obierz, rozdrobnij i przełóż do garnka. 	Dodaj pokrojonego ziemniaka, wlej litr gorącej wody, dodaj kostkę Knorr i gotuj wszystko aż zmięknie. 	Po tym czasie zupę zmiksuj na gładki krem i przelej na talerz. 	Namaluj wzorek pajęczyny. Na środku nałóż łyżką gęstą śmietanę. Wykałaczką rozciągnij śmietankę tak aby powstała pajęczyna.Do zupy możesz dodaj kilka groszków ptysiowych, które mogę udawać drobne muszki uwięzione w pajęczynie." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zupa chrzanowa z jajkiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zupa-chrzanowa-z-jajkiem.jpg", Time = 60, Number_Person = 4, Steps = 5, Instruction = " Cebule drobno pokrój w kostkę i podsmaż w garnku na oleju. 	 Gdy cebula zrobi się szklista dodaj mąkę i całość zasmaż. 	Następnie wlej bulion, dodaj kiełbasę i przyprawę Knorr. Całość gotuj około 20 minut. Po tym czasie kiełbasę wyjmij, lekko wystudź i pokrój w plastry. 	Jaja ugotuj na twardo, następnie posiekaj lub zetrzyj na tarce. 	Do wywaru dodaj chrzan oraz śmietanę i gotuj ponownie około 15 minut, aż zupa przejdzie smakiem chrzanu. 	Tuż przed podaniem dodaj do zupy pokrojoną kiełbasę oraz posiekane jajka. Podawaj z natką pietruszki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "aksamitna zupa z bobu ze szparagowa nuta", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/aksamitna-zupa-z-bobu-ze-szparagowa-nuta.jpg", Time = 60, Number_Person = 4, Steps = 5, Instruction = " Rozpuść kostki Rosołu z kury z pietruszką i lubczykiem Knorr w 1,5 litra wrzącej wody. 	 Umyj, osusz i pokrój sałatę w paski. Umyj i odsącz zieloną fasolkę szparagową.	Na średnim ogniu rozpuść masło w garnku z grubym dnem. Podsmaż w nim sałatę przez około 3 minuty. Należy pamiętać, aby masło nie przypaliło się, inaczej potrawa będzie niesmaczna i niezdrowa. Wlej bulion i zagotuj. Dodaj bób i 2/3 fasolki. Zagotuj całość i kontynuuj gotowanie przez około 20 minut.	Ugotuj w tym czasie 1/3 fasolki, uważając, by jej nie rozgotować. Wyjmij kilka ziaren bobu do dekoracji (kiedy bób z fasolką i sałatą są miękkie).	Resztę zmiksuj, ale nie zbyt mocno, by zupa miała bardziej wiejski charakter. Dodaj śmietankę, dopraw pieprzem i solą do smaku (jeśli uznasz to za konieczne).	Dodaj szczyptę gałki muszkatołowej. Wymieszaj i gotuj na małym ogniu przez około 2 minuty. Podawaj z fasolką oraz kilkoma ziarnami bobu na wierzchu. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "biala kielbasa w sosie piwnym", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/biala-kielbasa-w-sosie-piwnym.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Cebule i czosnek pokrój w bardzo drobną kostkę. Następnie podsmaż na rozgrzanym oleju najlepiej w rondlu.	 Dodaj kiełbasę i również podsmaż, aż skórka zrobi się rumiana. Następnie oprósz całość mąką i dokładnie wymieszaj.	Wlej do rondla z kiełbasą piwo i zagotuj. 	Dodaj śmietanę, chrzan, majeranek, liść laurowy oraz przyprawę Knorr. Całość gotuj powoli około 10 minut, aż kiełbasa zrobi się miękka i sos lekko zgęstnieje. Podawaj z tłuczonymi ziemniakami i posypane siekaną natką pietruszki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 3, Name_Recipe = "orzezwiajaca herbata z mieta i limonka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/orzezwiajaca-herbata-z-mieta-i-limonka276305.jpg", Time = 15, Number_Person = 1, Steps = 2, Instruction = " Zaparz czarną herbatę i odczekaj, aż ostygnie. Pokrój limonkę na cząstki. Wrzuć ją do szklanki razem z listkami mięty i brązowym cukrem. 	 Ugnieć wszystkie składniki, dodaj herbatę i wodę gazowaną. 	Na koniec dodaj lód. Udekoruj świeżą miętą. Smacznego! Ciepło, cieplej, gorąco… i zimna herbata. Sprawdź, jak przygotować idealną herbatę na upały." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 3, Name_Recipe = "herbata z sorbetem cytrynowy raj", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/lipton-yellow-label-z-sorbetem-cytrynowym970264.jpg", Time = 5, Number_Person = 2, Steps = 2, Instruction = " Dwie torebki herbaty Lipton zalej 400 ml gotującej się wody. Napar przykryj spodkiem odstaw na bok aby dobrze się zaparzył. Herbatę wystudź. 	 Zrób kulki z sorbetu cytrynowego Carte d'or, wstaw je do zamrażalki aby mocno się zmroziły. 	Do słoika wlej zimny napar z herbaty dodaj plastry limonki i cytryny oraz kulkę sorbetu.Herbatę podawaj z gałązką rozmarynu." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 3, Name_Recipe = "herbata malinowa", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/rozgrzewajaca-herbata-malinowa644878.jpg", Time = 15, Number_Person = 1, Steps = 2, Instruction = " Przygotuj napar z czarnej herbaty. 	 Dodaj do niego sok z malin, konfiturę i owoce (jeżeli używasz mrożonych malin, podgrzej je wcześniej na wolnym ogniu, aż zupełnie zmiękną). Jeśli nie masz malin możesz użyć konfitury malinowej.	Dokładnie wymieszaj napój. Smacznego! Chłód zimowych wieczorów przegoni herbata z malinami. Już pierwszy łyk otoczy Cię przyjemnym malinowym ciepłem i aromatem." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "pulpeciki w sosie pomidorowym", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/pulpeciki-w-sosie-pomidorowym.jpg", Time = 60, Number_Person = 4, Steps = 5, Instruction = " Cebulę pokrój w drobną kostkę i podsmaż. Bułkę namocz w mleku	 Mięso wymieszaj z podsmażoną cebulą, jajkiem oraz odciśniętą bułką. Dopraw solą. 	Następnie uformuj małe kuleczki i usmaż na rozgrzanym oleju.	Zetrzyj na tarce marchewkę i pietruszkę. 	Zagotuj wodę ze startymi warzywami i koncentratem pomidorowy.	W niewielkiej ilości wody wymieszaj sos Knorr. Wraz z pulpecikami dodaj do warzyw. Całość gotuj kilka minut, aż sos zgęstnieje, warzywa będą miękkie, a pulpeciki gorące." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "golonka po bawarsku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/golonka-po-bawarsku947567.jpg", Time = 90, Number_Person = 4, Steps = 3, Instruction = " Mięso oczyść i ponacinaj. Natrzyj dokładnie przyprawą Knorr oraz czosnkiem. Odstaw, na co najmniej 2 godziny. 	 Poszatkuj cebule. Do garnka zetrzyj warzywa, dodaj cebule, całość zalej 2 litrami wody. Dodaj przyprawy. Obgotuj w takim wywarze golonki przez 30 minut. 	Następnie przełóż mięso do brytfanki, podlej wywarem wyławiając większość warzyw i piecz w rozgrzanym do 180 stopni piekarniku przez godzinę. 	Po tym czasie polej golonki piwem i piecz jeszcze prze godzinę, polewając od czasu do czasu sosem z pieczenia, do momentu, kiedy mięso zrobi się rumiane. Podawaj z chlebem, ziemniakami lub kluskami." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "nozki kurczaka z paprykowej marynaty", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/nozki-kurczaka-z-paprykowej-marynaty34006.jpg", Time = 60, Number_Person = 4, Steps = 2, Instruction = " Rozgnieć ząbki czosnku i wyciśnij sok z cytryny. Wymieszaj z oliwą, dodaj słodką i ostrą paprykę oraz przyprawę Knorr. Ponownie wszystko dokładnie wymieszaj.	 Zanurz mięso w przygotowanej marynacie. Dodaj zioła i lekko wymieszaj, aby zioła dobrze przylgnęły do mięsa. Całość zostaw w chłodnym miejscu na minimum godzinę.	Połóż kawałki zamarynowanego mięsa na ruszt grilla. Piecz powoli przez około 30 minut, przekręcając od czasu do czasu, aby każda strona mięsa równomiernie się usmażyła. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "torcik z winogronami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/torcik-z-winogronami.jpg", Time = 60, Number_Person = 1, Steps = 6, Instruction = " Cukier wsypać do teflonowego garnka. Stale mieszając drewnianą łyżką zrumienić na ogniu, a następnie dodać Kasię i 5 łyżek śmietanki. Doprowadzić do wrzenia. Nie przerywając mieszania, zdjąć z ognia, lekko schłodzić i wymieszać z płatkami migdałowymi i kukurydzianymi. 	 Delikatnie nałożyć masę do tortownicy wyłożonej folią aluminiową.	Galaretkę rozpuścić w szklance wody i ostudzić.	Śmietanę schłodzić i ubić ze szczyptą soli na sztywno.	Serek, wino i sok z cytryny wymieszać, dodać galaretkę, a gdy masa zacznie tężeć, wymieszać z ubitą kremówką.	Chłodną masę wyłożyć na chrupiący spód i wstawić na kilka godzin (najlepiej na noc) do lodówki.	Winogrona maczać w białku i obtaczać w cukrze, przybrać nimi tort i... podawać. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "watrobka drobiowa", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/watrobka-drobiowa.jpg", Time = 45, Number_Person = 4, Steps = 5, Instruction = " Cebule pokrój w plastry, jabłka obierz i pokrój w cząstki, a śliwki pokrój na mniejsze kawałki. Wszystko razem podsmaż na patelni i przełóż na talerz. 	 Wątróbkę umyj, oczyść z żyłek, po czym oprósz w mące i obsmaż na patelni. 	Całość zalej sosem do pieczeni Knorr, rozmieszanym w 200 mililitrach wody. Dopraw majerankiem i gotuj jeszcze 8 minut.	Następnie dodaj do niej wszystkie podsmażone wcześniej składniki.	Przygotuj sałatkę: Sos vinaigrette z opakowania Fixu Knorr wymieszaj z 3 łyżkami oliwy z oliwek i 3 łyżkami wody.	Kapustę pokrój w cienkie plastry, marchew zetrzyj, a orzechy rozkrusz. Dokładnie je wymieszaj. Całość zalej przygotowanym sosem sałatkowym i ponownie wymieszaj. " },
