                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka grecka inaczej", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-grecka-inaczej.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Sos Knorr przygotuj według wskazówek umieszczonych na opakowaniu. 	 Radicchio porwij na mniejsze kawałki, papryki oraz ser pokrój w kostkę 1,5 x 1,5 centymetra. Cebulę natomiast pokrój w cienkie krążki.	W misce wymieszaj sałaty, paprykę oraz oliwki – następnie przełóż do miski, w której podasz sałatkę. Na wierzchu ułóż krążki cebuli, posyp pokrojonym serem. Całość zalej przygotowanym sosem i oprósz suszonym oregano. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "biszkopt z malinami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/biszkopt-z-malinami.jpg", Time = 15, Number_Person = 1, Steps = 4, Instruction = " Białka ubić, dodać cukier, potem żółtka, na końcu wymieszać z mąką zmieszana z proszkiem, piec ok. 40 min w temp. 160-180 C, pozostawić do wystygnięcia. 	 Ostudzony przekroić na dwie części.	Kasię zmiksować, dodając stopniowo puder, dodac maliny, zmiksować.	Rozpuścić galaretki.	Na biszkopt wyłożyć większą część masy malinowej, przykryć drugim biszkoptem, wyłożyć resztę masy, ułożyć delicje, zalać tężejącą galaretką, schłodzić " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka makaronowa z rzodkiewka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-makaronowe-zamieszanie.jpg", Time = 15, Number_Person = 4, Steps = 4, Instruction = " Umyj sałatę i rzodkiewki. 	 Pokrój szynkę w kostkę, rzodkiewki w plastry, natomiast szczypiorek posiekaj.	Porwij sałaty, a następnie wymieszaj je z makaronem i pozostałymi składnikami.	Dodaj Sos sałatkowy włoski Knorr przygotowany według przepisu na opakowaniu.	Gotową sałatkę podawaj posypaną pestkami słonecznika. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "bialo czerwone", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/bialo-czerwone.jpg", Time = 30, Number_Person = 1, Steps = 3, Instruction = " Białka z cukrem ubić na sztywno, dodać żółtka, mąkę i proszek do pieczenia. 	 Upiec biszkopt ok. 40 minut.	Upieczone ciasto przekroić na pół, posmarować jedną powierzchnię ciasta bitą śmietaną oraz owocami kandyzowanymi.	Nałożyć na to drugą część ciasta, na wierzchu posmarować bitą śmietaną, dodać owoce kandyzowane oraz galaretkę. " },
