                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kurczak po meksykansku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kurczak-po-meksykansku-2.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Piersi z kurczaka i cebulę pokrój w kostkę, podsmaż w garnku w małej ilości tłuszczu. 	 Dodaj czerwoną fasolę, kukurydzę i pokrojoną w kostkę czerwoną paprykę.	Fix rozmieszaj w 300 mililitrach zimnej wody i dodaj do potrawy. Chwilę gotuj.	Gotowe danie podawaj z ugotowanym ryżem. " },
