                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zupa gulaszowa z papryka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zupa-gulaszowa-z-papryka.jpg", Time = 90, Number_Person = 4, Steps = 5, Instruction = " Przygotuj bulion z 2 kostek Knorr i 1 l wody. Wystudź. 	 Wołowinę pokrój w drobną kostkę, a następnie zmiel.	Papryki i cebulę pokrój w kostkę. Czosnek drobno posiekaj.	Mięso podduś na oleju z cebulą i czosnkiem. Dodaj liść laurowy, ziele angielskie i pokrojoną paprykę oraz pokrojone pomidorki cherry.	Zalej 0,75 litra bulionu i gotuj do czasu, gdy mięso zmięknie.	Pozostałą część bulionu wymieszaj z mąką i dodaj do zupy. Chwilę gotuj." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zupa cebulowa na smietanie", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zupa-cebulowa-na-smietanie.jpg", Time = 30, Number_Person = 4, Steps = 5, Instruction = " Przygotuj wywar z wody i bulionetki Knorr. 	 Posiekaj cebulę, następnie ją usmaż.	Dodaj koncentrat pomidorowy i paprykę, lekko podsmaż.	Dodaj do wywaru przesmażoną cebulę i gotuj 10 minut. Dla wzmocnienia smaku możesz dodać bulionetkę Knorr	Przetrzyj zupę.	Połącz śmietanę z mąką. Dodaj śmietanę zmieszaną z mąką do zupy, gotuj kilka minut i dopraw do smaku cukrem i gałką muszkatołową.Zupę cebulową na śmietanie podawaj posypaną natką pietruszki z kluseczkami." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "gulaszowa z wolowina", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/gulaszowa-z-wolowina.jpg", Time = 90, Number_Person = 4, Steps = 4, Instruction = " Mięso zalej gorącą wodą, dodaj włoszczyznę, przyprawy oraz kostkę Knorr i gotuj wywar.	 Po ugotowaniu odcedź mięso i pokrójw kostkę. Cebulę drobno posiekaj, podsmaż na smalcu, dodaj mięso izrumień.	Na patelnię wrzuć pokrojoną w paski paprykę. Gdy zmięknie, dodaj pokrojone pomidory i wszystko przesmaż. 	Teraz przełóż całość do garnka, dodaj pokrojone w kostkę ziemniaki i zalej wcześniej ugotowanym wywarem.	Dodaj zupę gulaszową Knorr i wszystko razem gotuj. Kiedy danie lekko zgęstnieje, a składniki będą miękkie, podawaj – najlepiej z pieczywem." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "tradycyjny barszcz czerwony ekspresowy", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/tradycyjny-barszcz-czerwony-ekspresowy.jpg", Time = 60, Number_Person = 4, Steps = 4, Instruction = " Obierz buraki i pokrój w plastry. Dodaj dwa ząbki czosnku i chleb razowy. Zagotuj 1 litr wody z przyprawami, zalej nim przygotowane buraki. Całość odstaw na około dwa tygodnie w ciepłe miejsce, aby powstał zakwas. 	 Przecedź wywar i dodaj do niego Barszcz czerwony ekspresowy Knorr, co poprawi kolor i nada zupie odpowiedni smak. 	Całość dopraw do smaku majerankiem, cukrem i sokiem z cytryny.	Zalej warzywa powstałym zakwasem. Dodaj buraki, przyprawy, resztę czosnku i ugotuj buraczano - warzywny wywar.	Podawaj barszcz z kołdunami mięsnymi lub grzybowymi. Barszcz powinien być gotowany na bardzo małym ogniu i najlepiej pozostawiony z warzywami na drugi dzień, aby naciągnął ich smakiem i aromatem." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kacze uda po gdansku w pomaranczach", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kacze-uda-po-gdansku-w-pomaranczach-2.jpg", Time = 90, Number_Person = 4, Steps = 4, Instruction = " Umyte i osuszone udka z kaczki skrop sokiem z cytryny, oprósz przyprawą Knorr oraz otartąskórką z 2 pomarańczy, odstaw na bok przynajmniej na jedną godzinę. 	 Włoszczyznę umyj, obierz i pokrój, podsmaż na rozgrzanym oleju, po czym włóż do żaroodpornego naczynia. Na warzywach ułóż udka z kaczki.	Całość zalej szklanką wody, dodaj liść laurowy, ziele angielskie i żeby danie miało pełny smak - bulionetkę. Kaczkę duś w nagrzanym do 180 st. C piekarniku przez około 90 minut do momentu aż będzie zupełnie miękka.Jeśli woda odparuje uzupełnij ją na powrót. Udka przełóż do oddzielnego naczynia, przecedzając jednocześnie do odpowiedniego garnuszka wywar z pieczenia. 	Zobranych pomarańczy wykrój segmenty, zbierając jednocześnie powstały sok. Sok, wywar z duszenia połącz z likierem pomarańczowym i zagotuj. 	Z masła i mąki zrób zasmażkę i całość zagęść dodając ją do sosu. W ostatniej chwili do gotowego już sosu dodaj segmenty z pomarańczy. Upieczone udka podawaj polane sosem. Do pieczenia kaczki można dodać dodatkowo laskę cynamonu oraz kawałek świeżego imbiru. Przyprawy te idealnie komponują się z aromatem pomarańczy." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "pieczona szynka w sosie cebulowym", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/pieczona-szynka-w-sosie-cebulowym.jpg", Time = 90, Number_Person = 4, Steps = 3, Instruction = " Mięso natrzyj Przyprawą do mięs, obsmaż na patelni na rozgrzanym oleju. 	 Mięso przełóż na blachę, podlej niepełną szklanką wody, piecz w rozgrzanym do 190°C piekarniku przez około 90 minut.	Na tej samej patelni, na której było pieczone mięso, na rozgrzanym tłuszczu podsmaż pokrojoną w piórka cebulę.	Dodaj 200 ml zimnej wody. Patelnię zestaw z ognia i dodaj esencję do sosu Knorr, dzięki której sos będzie gęsty i dobrze doprawiony. Całość dokładnie wymieszaj, upewniając się, że esencja zupełnie się rozpuściła. Wszystko razem gotuj dwie minuty. Szynkę podawaj z sosem. Szczypta tymianku idealnie skomponuje się ze smakiem cebuli." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "pieczony kurczak z ziemniakami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/pieczony-kurczak-z-ziemniakami.jpg", Time = 60, Number_Person = 4, Steps = 2, Instruction = " Stwórz błyskawiczną marynatę, mieszając przyprawę Knorr z olejem, papryką i oregano. Natrzyj nią kurczaka z zewnątrz i od środka. Jeśli lubisz potrawy pikantne, do marynaty dodaj również trochę ostrej papryki. 	 Ziemniaki obierz i przekrój na cztery części.	Kurczaka i ziemniaki umieść w brytfance, piecz 60 minut w 190 °C. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 3, Name_Recipe = "lady grejpfrut herbata z grejpfrutem i rozmarynem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/lady-lipton-z-czerwonym-grejpfrutem513430.jpg", Time = 5, Number_Person = 2, Steps = 2, Instruction = " Dwie torebki herbaty Lipton Lady Lipton i starty, świeży imbir zalej 400 ml gotującej się wody. Przykryj spodkiem i odstaw na chwilę aby napar dobrze się zaparzył. Po chwili dodaj dwie łyżki miodu całość dokładnie wymieszaj.	 Napar przelej przez sitko i wlej do szklanki z plastrami grejpfruta i gałązką rozmarynu . 	Szklankęudekoruj gałązką rozmarynu i skrawkiem skórki grejpfruta.Do tej herbaty można również dodać kawałek ostrej papryczki chili." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z rukola i krewetkami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-rukola-i-krewetkami-video.jpg", Time = 30, Number_Person = 4, Steps = 4, Instruction = " Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem oraz ketchupem. 	 Pomidory pokrój w ćwiartki, usuń z nich gniazda nasienne, następnie pokrój w cienkie paski. Suszone pomidory również pokrój w paski.	Mango obierz, pokrój w plastry, a potem w paski. 	Krewetki parz chwilę we wrzącej wodzie. Pomidory, mango i sałatę wymieszaj z dresingiem i rozłóż na talerze. Na wierzch połóż krewetki. Orzeszki piniowe upraż na patelni, ser pleśniowy pokrój w kostkę.	Sałatkę posyp grzankami, orzeszkami piniowymi oraz serem pleśniowym. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kociolek gulaszowy z kasza", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kociolek-gulaszowy-z-kasza.jpg", Time = 60, Number_Person = 4, Steps = 2, Instruction = " Mięso, paprykę i marchew pokrój w grubą kostkę. Cebulę posiekaj, podziel na dwie części. Jedną z nich podsmaż na oleju, dodaj mięso i smaż, aż będzie zupełnie suche. Dorzuć marchew i paprykę, smaż kolejną chwilę, mieszając co jakiś czas. 	 Produkt Knorr Naturalnie Smaczne - Gulasz wymieszaj z 400 ml wody i wlej do garnka. Duś pod przykryciem na wolnym ogniu, aż mięso będzie zupełnie miękkie.	Kaszę ugotuj. Na patelni podsmaż pozostałą cebulę, dodaj kaszę i Esencję do duszonych mięs Knorr. Całość dokładnie wymieszaj, aby kostka się rozpuściła. Kaszę możesz doprawić odrobiną majeranku. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka napoli z awokado video", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-napoli-z-awokado-video.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem i startym serem. 	 Rozgrzej patelnię bez dodatku oleju. Orzeszki pinii upraż na złoty kolor.	Awokado obierz, pokrój na kawałki, skrop sokiem z cytryny, aby nie ściemniało.	Umyj i osusz sałaty. Potem przełóż je do salaterki lub na talerz, ułóż awokado, pokrusz ser pleśniowy i polej sosem. Całość posyp uprażonymi orzeszkami pinii i grzankami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "gulasz wegierski z kurczakiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/gulasz-wegierski-z-kurczakiem.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Fix Knorr wymieszaj z 500 mililitrami wody.	 Piersi umyj, pokrój w dużą kostkę i oprósz solą i łyżeczką słodkiej papryki. W równie dużą kostkę pokrój cebule, papryki oraz ziemniaki. Pokrojone ziemniaki ugotuj na półtwardo w rondelku.	Na rozgrzanym tłuszczu przesmaż cebulę i rozgnieciony czosnek, dodaj sproszkowane papryki i kurczaka. Całość wymieszaj, smaż chwilę a następnie zalej przygotowanym Fixem i zagotuj.	Następnie dodaj pokrojoną paprykę oraz ziemniaki. Gotuj jeszcze chwilę, dopraw do smaku kminkiem, majerankiem oraz solą – jeśli zajdzie taka potrzeba. Podawaj z pieczywem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z kiwi i cykorii", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-kiwi-i-cykorii.jpg", Time = 30, Number_Person = 4, Steps = 4, Instruction = " Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem, ketchupem, oraz sokiem pomarańczowy. Z cykorii odetnij końcówki, rozłóż wszystkie liście, umyj je i osusz. Zachowaj kilka całych liści cykorii do dekoracji. Resztę pokrój po skosie w dwucentymetrowe rąby i umieść w misce z dresingiem. Dosyp do nich grzanki. 	 Kiwi obierz, pokrój w półplastry i dodaj do cykorii.	Ser żółty pokrój w grubą kostkę i przełóż do miski.	Posiekaj natkę kolendry i cebulę w kostkę, następnie dodaj do reszty składników. 	Do miski dodaj rodzynki i całość dokładnie wymieszaj. Podawaj w małych miseczkach udekorowane listkami cykorii oraz grzankami Knorr." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "puszysty sernik", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/puszysty-sernik.jpg", Time = 60, Number_Person = 1, Steps = 8, Instruction = " Mąkę zmieszać z proszkiem do pieczenia i kakao. 	 Żółtka i cukier roztrzepać w śmietanie.	Dodać do mąki i Kasi - wyrobić ciasto.	Włożyć do zamrażalnika do wychłodzenia (powinno się lekko stwardnieć, aby lepiej się tarło).	Kasię utrzeć z cukrem i żółtkami na puszystą masę.	Stopniowo dodawać ser i ubitą uprzednio śmietanę, następnie dodać budyń, sok z cytryny i rodzynki.	Ubić pianę z białek i delikatnie połączyć z masą serową .	Większą połowę ciasta zetrzeć do formy(24 x 34cm) wyłożonej papierem do pieczenia na tarce (na jarzynkę), wylać masę serową, a na to zetrzeć pozostałe ciasto.	Piec 60 minut w temperaturze 170 C.0Po upieczeniu i wystudzeniu sernik polukrować. Smacznego! " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z soczewicy", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-soczewicy.jpg", Time = 45, Number_Person = 4, Steps = 2, Instruction = " Soczewicę ugotuj w wodzie z dodatkiem kostki Bulionu na włoszczyźnie Knorr. Następnie odcedź i ostudź. Cebule, czosnek i natkę pietruszki posiekaj.	 Obierz i pokrój w kostkę jabłko i ogórki, wszystkie składniki sałatki połącz.	Dodaj majonez, przypraw pieprzem i wymieszaj." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "smazone kotleciki jagniece", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/smazone-kotleciki-jagniece.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Mięso umyj, oczyść z nadmiaru tłuszczu i zbędnych błon. Pośrodku polędwicy, wzdłuż całej jej długości, zrób szerokie nacięcie. 	 Orzeszki podsmaż na patelni, ostudź. Pokrój ser feta, oliwki i suszone pomidory w małą kostkę, pokrój chleb tostowy. Dodaj wszystko do orzeszków. Tak przygotowanym farszem nadziej comber.	Wierzch mięsa oprósz Naturalną Przyprawą do mięs Knorr, kolendrą i kuminem.	Smaż na grillu około 20 minut, co jakiś czas przekręcając mięso. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "torcik budyniowy z owocami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/torcik-budyniowy-z-owocami.jpg", Time = 60, Number_Person = 1, Steps = 8, Instruction = " Budyń rozpuścić w 1 szklance mleka, drugą zagotować z cukrem i połączyć z rozpuszczonym budyniem. 	 Zagotować i ostudzić.	Żółtka zmiksować z Kasią i dodawać stopniowo budyń, mąkę ziemniaczaną, alkohol i aromat.	Masę dzielimy w proporcji 3/4 i 1/4 części.	Do 3/4 masy dodajemy zmiksowane owoce (1-2 łyżeczki).	Na spód tortownicy położyć herbatniki, biszkopty lub bezy i wylać 3/4 masy ze zmiksowanymi owocami.	Ułożyć owoce na masę, zalać galaretką i odstawić do ostudzenia.	Gdy galaretka zastygnie, położyć na nią pozostałe 1/4 masy bez owoców, wyrównać powierzchnię i udekorować małymi bezikami lub kleksami z masy budyniowej, ewentualnie owocami.	Wstawić do lodówki " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z zielonego groszku i pomaranczy", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-zielonego-groszku-i-pomaranczy.jpg", Time = 30, Number_Person = 4, Steps = 1, Instruction = " W dużej misce przygotuj sos, łącząc ze sobą sos Knorr, oliwę, ketchup, majonez i sok pomarańczowy. 	 Rozmroź groszek. Czerwoną cebulę i paprykę pokrój w kostkę, pomidory przekrój na pół, pomarańcze obierz i pokrój, a miętę posiekaj. Wszystkie składniki dołóż do miski z sosem. Dodaj groszek oraz pestki słonecznika. Całość wymieszaj i posyp grzankami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "azjatycka zupa", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/azjatycka-zupa.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Mięso opłucz, pokrój w kostkę, przełóż do garnka, zalej 1,2 l wody i gotuj przez 10 minut. 	 Grzyby, cebulę oraz marchewkę pokrój w paski i dodaj do wywaru z mięsem.	Fix Knorr wymieszaj z 300 ml wody i dodaj do wywaru z mięsem oraz warzywami. Gotuj 5 minut.	Po tym czasie dodaj do zupy posiekany szczypiorek oraz kiełki. Wszystko dokładnie wymieszaj, dopraw sosem sojowym, nie gotuj – natychmiast podawaj.Rosół azjatycki możesz również serwować z makaronem lub ryżem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "chery", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/chery.jpg", Time = 60, Number_Person = 1, Steps = 7, Instruction = " Białka ubić, dodać cukier i żółtka, następnie pozostałe składniki i upiec biszkopt (ok. 25 minut w 180C). 	 Wystudzić, przekroić na pół.	Wiśnie odcedzić.	Sokiem delikatnie nasączyć biszkopt.	Krem przygotować według polecenia na opakowaniu, stosując wersję bez masła, na koniec wymieszać z wiśniami i przełożyć na biszkopt.	Śnieżkę ubić z mlekiem, dodając do niej rozpuszczoną letnią żelatynę (2 łyżeczki w 1/3 szkl gorącej wody).	Masę krówkową utrzeć z 1/2 kostki Kasi, rozprowadzić po wierzchu ciasta na przemian ze Śnieżką, posypać płatkami migdałowymi.	Przełożenie: biszkopt-krem z wiśniami-biszkopt-śnieżka-krówka-płatki migdałowe " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z pasta serowa i winogronami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-pasta-serowa-i-winogronami.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Ser pleśniowy rozgnieć widelcem i połącz z twarożkiem i majonezem aż powstanie gładka pasta.	 Chleb pokrój i posmaruj najpierw margaryną, a potem pastą serową.	Winogrona pokrój na ćwiartki i udekoruj kanapki. Kanapki możesz udekorować orzechami." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zapiekanka z makaronu", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zapiekanka-z-makaronu.jpg", Time = 60, Number_Person = 6, Steps = 4, Instruction = " Makaron ugotuj al dente. Odcedź, przelej zimną wodą i odsącz. Cebule obierz, pokrój razem z szynką w kostkę. Pomidory umyj i pokrój na ćwiartki, usuń gniazda nasienne. Jeden pomidor zostaw do dekoracji. 	 Cebulę podsmaż na oleju, dodaj szynkę. Wrzuć pomidory, dodaj kostkę Rosołu z kury Knorr i wszystko razem chwilę podduś.	Połowę makaronu i połowę szczypiorku zwiń w kształcie koła w okrągłymżaroodpornym naczyniu wysmarowanym tłuszczem.	Na makaron wyłóż masę z szynką i cebulą. Przykryj resztą makaronu i szczypiorku.	Kwaśną śmietanę rozmieszaj z jajkami, dodaj utarty ser. Udekoruj pozostawionym pomidorem pokrojonym w ćwiartki. Piecz przez ok. 60 minut w nagrzanym do 175 stopni piekarniku." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "faszerowany baklazan a la gyros", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/faszerowany-baklazan-a-la-gyros.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Cebulę oraz czosnek posiekaj. Kurczaka pokrój w drobną kostkę i podsmaż wszystko na patelni. Dopraw mięso przyprawą do złotego kurczaka. 	 Do mięsa dodaj posiekane pomidory z puszki razem z sokiem oraz Fix Knorr. Wszystko wymieszaj i duś, aż sos zgęstnieje. 	W tym czasie umyj dokładnie bakłażana, odetnij końcówki i przetnij go wzdłuż na pół. Następnie wytnij małym nożykiem miąższ. 	Teraz włóż do bakłażana farsz z kurczaka. Całość zapiekaj przez 15 minut w 180 °C. Danie bardzo dobrze smakuje z sosem jogurtowym." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "sloneczne kanapki z warzywami do szkoly", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/sloneczne-kanapki-z-warzywami-do-szkoly-2.jpg", Time = 15, Number_Person = 2, Steps = 3, Instruction = " Jajko ugotuj na twardo. Bajgle lub rogaliki przekrój wzdłuż na pół i posmaruj margaryną Rama. 	 Kanapkę posmaruj grubszą warstwą kremowego twarożku. 	Wszystkie warzywa oraz jajko pokrój w plasterki i ułóż na serku, tak aby lekko się przykleiły. 	Całość przykryj drugą kromką pieczywa. Kanapki spakuj np. do szkolnego pudełka na drugie śniadanie. Do kanapki możesz również dodać ogórka konserwowego." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z grillowanym kurczakiem i awokado", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-grillowanym-kurczakiem-i-awokado.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Pieczywo pokrój na cienkie kromki i opiecz, aby były chrupkie. Następnie wystudź i posmaruj cienką warstwą margaryny Rama. 	 Liście sałaty umyj i osusz, rozłóż na każdej kromce. Kurczaka, obrane awokado oraz pomidora pokrój w drobną kostkę.	Pokrojone składniki połącz ze sobą, dodaj oliwę oraz pokrojoną kolendrę i sok z limonki.	Wszystko delikatnie wymieszaj i powstałą salsę rozłóż na kromkach pieczywa. Kanapki udekoruj listkami kolendry. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z pasta czosnkowa i ziolami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-pasta-czosnkowa-i-ziolami.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Czosnek rozgnieć i wymieszaj z twarożkiem, majonezem, posiekaną natką pietruszki i ziołami prowansalskimi. 	 Bułeczki przekrój na pół i posmaruj margaryną, następnie nałóż pastę. 	Gotowe kanapki możesz udekorować gałązkami natki pietruszki.Do pasty możesz dodać ulubione zioła. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z pieczona wolowina rukola i marynowana papryka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-pieczona-wolowina-rukola-i-marynowana-papryka.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Pieczywo pokrój w nieduże kromki a następnie posmaruj cienko margaryną Rama. 	 Na każdej kromce rozłóż po kilka liści rukoli. Upieczoną wołowinę pokrój w cienkie plastry, zwiń dekoracyjnie każdy plaster i rozłóż na kanapkach.	Paprykę odsącz i pokrój w cienkie paseczki. Ułóż na kanapkach i udekoruj listkami natki. Aby zaostrzyć smak kanapek, nałóż na każdą z nich odrobinę francuskiej musztardy." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "piers kurczaka z pieczarkami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/piers-kurczaka-z-pieczarkami94866.jpg", Time = 45, Number_Person = 4, Steps = 3, Instruction = " Pokrój mięso, umyj i osusz. Dopraw przyprawą Knorr i podsmaż na rozgrzanym oleju wraz z połówkami pieczarek.	 Zetrzyj sery i podgrzewaj razem z mięsem na patelni, do momentu, aż się stopią. 	Rozprowadź po całości śmietanę i dodaj sos sałatkowy Knorr. 	Dobrze wymieszaj i duś całość około 5 minut. Na koniec dodaj posiekaną bazylię i świeżo zmielony pieprzTak przygotowane mięso doskonale smakuje z gotowanym ryżem lub ziemniakami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "piersi z kurczaka nadziewane boczniakami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/piersi-z-kurczaka-nadziewane-boczniakami.jpg", Time = 60, Number_Person = 4, Steps = 4, Instruction = " Oczyść piersi z kurczaka, umyj i osusz. Podziel na 4 porcje. W każdej z nich natnij kieszeń.	 Naturalną przyprawę do kurczaka Knorr wymieszaj z 2 łyżkami oliwy i natrzyj nią piersi z kurczaka. Wstaw do lodówki na około 30 minut do zamarynowania.	Cebulę posiekaj, mozzarellę pokrój w plastry. Boczniaki umyj i osusz, pokrój w paski. Posiekaj natkę pietruszki. 	Na rozgrzanej 1 łyżce oliwy zeszklij cebulę. Dodaj boczniaki i smaż kilka minut. Dopraw pieprzem i solą do smaku, jeśli uznasz, że jest to konieczne. Oprósz natką pietruszki. Odstaw do przestygnięcia. 	W kieszenie kurczaka wkładaj po plastrze sera i łyżce nadzienia grzybowego. Zepnij wykałaczkami. Smaż na rozgrzanym grillu, do momentu kiedy mięso będzie usmażone. Podawaj z sałatami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "sznycle z indyka w polewie grzybowej", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/sznycle-z-indyka-w-polewie-grzybowej.jpg", Time = 30, Number_Person = 4, Steps = 4, Instruction = " Sznycle dopraw pieprzem oraz solą, następnie podsmaż lekko z obu stron. 	 Dodaj cebulę, podduś chwilę, po czym zalej winem i gotuj na małym ogniu - do momentu, kiedy połowa płynu odparuje.	Podsmaż oddzielnie pieczarki i dodaj je do mięsa.	Zalej mięso i warzywa Sosem borowikowym Knorr, przygotowanym według przepisu na opakowaniu.	Na koniec dodaj starty ser i gotuj całość na małym ogniu do czasu, gdy się on roztopi.Podawaj sznycle z makaronem typu wstążki, posypane natką pietruszki." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "francuskie rogaliki z szynka i serem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/francuskie-rogaliki-z-szynka-i-serem.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Rogaliki croissant przekrój delikatnie wzdłuż na pół i posmaruj margaryną Rama.	 Na dolną część rogalika połóż pokrojonego w plastry pomidora oraz plastry sera pleśniowego. 	Teraz nałóż szynkę oraz rukolę. 	Całość przykryj górną częścią rogalika i natychmiast podawaj.Do kanapek możesz również dodać kilka listów świeżej bazylii." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 4, Name_Recipe = "cappucino z grzybow lesnych i dachowki z parmezanu", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/cappuccino-z-grzybow-lesnych-i-dachowka-z-parmezanu2602696.jpg", Time = 30, Number_Person = 4, Steps = 5, Instruction = " Rozgrzej piekarnik do 180°C. Do rozgrzanego rondla wlej Ramę Smaż jak szef kuchni.Poczekaj aż z tłuszczu znikną bąbelki – to znak, że temperatura jest odpowiednia do smażenia. 	 Smaż szalotki ok. 2 minuty. Dodaj pieczarki i grzyby leśne i smaż kolejne 5 minut. Podlej bulionem, przykryj i gotuj na wolnym ogniu przez 15 minut.	Na papierze pergaminowym zrób krążki z tartego parmezanu o średnicy około 5 cm. Włóż je do rozgrzanego piekarnika na 10-15 minut – parmezan stopi się i przybierze piękny, złoty kolor. 	Wymieszaj mąkę z 50 ml śmietany tak, by nie było grudek. 	Zmiksuj zupę ugotowaną wcześniej, następnie wstaw ją ponownie na ogień. Dodawaj śmietanę z mąką, cały czas mieszając, aż zupa zacznie gęstnieć.	Resztę śmietany ubij na sztywno. Dopraw ją solą i pieprzem.Sposób podania: Weź szklankę do whisky, wlej zupę do 3 wysokości naczynia.Dodaj ubitą doprawioną śmietanę. Posyp pudrem grzybowym, dodaj czerwony pieprz i oliwę truflową. Złam dachówki z parmezanu i wbij je w śmietanę." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zielona kanapka z pasta z awokado i lososiem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zielona-kanapka-z-pasta-z-awokado-i-lososiem.jpg", Time = 15, Number_Person = 4, Steps = 4, Instruction = " Jaja ugotuj na twardo. Następnie wystudź je, obierz i przełóż do miski.	 Awokado obierz i wyjmij łyżką miękki miąższ. Teraz połącz awokado z jajkami za pomocą blendera lub widelca tak aby powstała pasta. 	Bagietkę przekrój wzdłuż na pół a następnie pokrój na mniejsze kawałki. Każdy kawałek posmaruj margaryną Rama z masłem i zieloną pastę z awokado. 	Teraz połóż na kanapkach kawałki wędzonego łososia. 	Pomidora pokrój w cienkie plasterki i połóż na łososiu. Kanapki udekoruj listkami rukoli i podawaj.Tak przygotowane kanapki możesz dodatkowo skropić oliwą z oliwek i podać z kaparami. " },
