                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapka z twarozkiem i salami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapka-z-twarozkiem-i-salami.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Bułeczki przekrój na pół i posmaruj margaryna Rama.	 Nałóż warstwę twarożku. 	Salami natnij z jednej strony i wywiń zawijając rogi do siebie. Powstałe rożki wtykaj w serek. Po 3-4 plasterki salami na kanapkę.Kanapki udekoruj kawałkami czerwonej papryki, oliwkami i natką pietruszki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z pasta z pieczonego lososia z marynowanymi warzywami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-pasta-z-pieczonego-lososia-z-marynowanymi-warzywami-i-francuska-musztarda.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Kromki pieczywa opiecz chwilę, aby były chrupkie. Następnie wystudź i posmaruj cienką warstwą margaryny Rama. 	 Upieczonego łososia rozgnieć dokładnie w miseczce widelcem, dodaj majonez, sok z cytryny, posiekane kapary. Wszystko dokładnie wymieszaj tak, aby powstała pasta. 	Kromki pieczywa posmaruj przygotowaną pastą, na wierzchu ułóż kawałki pikli. Kanapki udekoruj sałatą, krążkami czerwonej cebuli oraz natką pietruszki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "gulasz w sosie pomidorowo smietanowym", Rate = 0, Level = "Średnie", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/gulasz-w-sosie-pomidorowo-smietanowym.jpg", Time = 30, Number_Person = 2, Steps = 2, Instruction = " Pokrój kurczaka na małe kawałki, następnie usmaż na oliwie na patelni. 	 Do garnka wlej Sos w słoiku Śmietanowo - Paprykowy Knorr. Dodaj suszone pomidory pokrojone w paski oraz wlej śmietanę. Zagotuj.	Do gorącego sosu dodaj mięso i duś jeszcze przez 10 minut. Jeśli uznasz, że sos jest zbyt gęsty możesz dolać trochę śmietany lub wody.Danie najlepiej podawaj z ryżem lub makaronem. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki na impreze z salami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-na-impreze-z-salami-2.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Z pieczywa wytnij kółka o średnicy 3-4 cm i posmaruj Margaryną Rama. 	 Na posmarowane kromki ułóż porwany liść sałaty oraz plasterki salami. 	Na każdą kanapkę połóż dekoracyjnie majonez. 	Na koniec udekoruj kanapki czerwoną cebulą, kaparami i listkami natki pietruszki. Jeśli lubisz indyjskie smaki majonez wymieszaj z curry." },
