                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kaszanka grillowana w jablkach", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kaszanka-grillowana-w-jablkach138377.jpg", Time = 40, Number_Person = 5, Steps = 3, Instruction = " Jabłka wydrąż z góry, odcinając wcześniej wierzchnią warstwę. 	 Na patelni, na rozgrzanej Ramie, podsmaż pokrojoną w plastry cebulę, dodaj rozdrobnioną kaszankę i Przyprawę do grilla Knorr. Następnie całość smaż, aż kaszanka się rozpadnie i zacznie delikatnie przywierać.	Tak przygotowaną kaszanką wypełnij jabłka.	Jabłka ułóż na dobrze rozgrzanym grillu pod przykryciem na około 20 minut. Zgrillowane jabłka podawaj z łyżką sosu Hellmann‘s Czosnkowy na wierzchu. Kaszankę możesz dodatkowo doprawić łyżką majeranku. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "spaghetti z chilli", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/spaghetti-z-chilli.jpg", Time = 15, Number_Person = 6, Steps = 4, Instruction = " Zagotuj w dużym garnku wodę i ugotuj makaron al dente. Rozgnieć ząbki czosnku. 	 Na patelni rozgrzej oliwę, wrzuć czosnek. Podsmaż do zezłocenia czosnku, wyjmij czosnek. 	Wrzuć filety anchois, rozgnieć widelcem i duś około 5 - 7 minut do ich rozpadnięcia.	Dodaj rozmieszany w wodzie Fix Pikantne Bolognese z chili Knorr i duś, aż sos zgęstnieje. 	Wymieszaj wszystko z makaronem, dodaj posiekaną natkę i podawaj. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "rumiane skrzydelka w miodowej bejcy", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/rumiane-skrzydelka-w-miodowej-bejcy.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Oczyść skrzydełka dokładnie. Umyj, osusz i odetnij końcówki.	 Wymieszaj dokładnie olej z miodem i Przyprawą Knorr. 	Posmaruj skrzydełka bejcą i odstaw na 2 godziny w chłodne miejsce. 	Piecz skrzydełka około 20 minut na dobrze rozgrzanym grillu, smarując je bejcą." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z rukola i krewetkami", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-rukola-i-krewetkami.jpg", Time = 15, Number_Person = 3, Steps = 4, Instruction = " Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem oraz ketchupem. 	 Pomidory pokrój w ćwiartki, usuń z nich gniazda nasienne, a następnie pokrój w paski.	Mango obierz, pokrój w plastry, a potem w paski.	Krewetki parz chwilę we wrzącej wodzie. Pomidory, mango i sałatę wymieszaj z dresingiem, rozłóż na talerze, a na wierzch połóż krewetki. Orzeszki piniowe upraż na patelni. 	Sałatkę posyp grzankami i orzeszkami piniowymi i pokrojonym w kostkę serem pleśniowym. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "tunczyk w orientalnym sosie videoSmażony makaron po chińsku Knorr", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/tunczyk-w-orientalnym-sosie-video.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Szalotkę obierz i przekrój na cztery części. Posiekaj paprykę. Batata obierz i przekrój wzdłuż na cztery części, a następnie pokrój w plastry. Ziemniaka zanurz na 5 minut w gotującej się wodzie. Do tej samej wody włóż na 5 minut makaron ryżowy.	 Rybę pokrój w grubą kostkę wielkości 1 cm. Podsmaż ją z obu stron na patelni i wyjmij. Na tej samej patelni obsmaż szalotkę, ostrą paprykę i batata. Dodaj groszek cukrowy i z powrotem przełóż na patelnię rybę.	Fix Knorr wymieszaj z wodą według proporcji podanych na opakowaniu. Zalej nim podsmażone produkty. Całość zagotuj i dopraw sokiem z limonki. 	Dodaj posiekany makaron ryżowy. Wszystko razem dokładnie wymieszaj. Danie podawaj posypane kawałkami szczypioru. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "szarlotka z galaretka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/szarlotka-z-galaretka51.jpg", Time = 60, Number_Person = 6, Steps = 5, Instruction = " Jabłka obierz, usuń gniazda nasienne i pokrój na ćwiartki. Owoce włóż do garnka, wlej 1/3 szklanki wody i upraż pod przykryciem. 	 Dwie galaretki rozpuść w szklance gorącej wody, dodaj Kasię i mieszaj, aż się rozpuści.	Rozpuszczone galaretki z Kasią dodaj do musu jabłkowego i wymieszaj. Odstaw do przestygnięcia.	Tortownicę o średnicy 21 cm posmaruj Kasią i wyłóż papierem do pieczenia. Ułóż na jej dnie warstwę biszkoptów.	Wystudzoną masę jabłkową wyłóż na biszkopty w formie i odstaw do lodówki by masa stężała.	Ostatnią galaretkę rozpuść w szklance gorącej wody i wystudź. Lekko tężejącą galaretkę wylej na wierzch szarlotki. Odstaw ponownie do lodówki. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "ziemniaki zapiekane z serem", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/ziemniaki-zapiekane-z-serem.jpg", Time = 60, Number_Person = 4, Steps = 3, Instruction = " Ziemniaki obierz i pokrój w cienkie plasterki. 	 W misce połącz zawartość opakowania Knorr ze śmietaną. Wsyp natkę, a następnie wymieszaj wszystko z pokrojonymi ziemniakami.	Cztery bulionówki natłuść margaryną i napełnij ziemniakami.	Wierzch każdej zapiekanki posyp startym serem i przykryj aluminiową folią. Naczynia wstaw do nagrzanego do 150 °C piekarnika i piecz przez 45 minut. Kilka minut przed końcem pieczenia zdejmij folię i ponownie zapiecz. Podawaj jako dodatek do dań gorących. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z twarozkiem pelne warzyw", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-twarozkiem-pelne-warzyw.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Pieczywo chrupkie posmaruj margaryną Rama z masłem.	 Następnie nałóż cienką warstwę kremowego serka. 	Rzodkiewki pokrój w bardzo cienkie plasterki, pomidorki w plastry, a szczypiorek posiekaj. 	Poukładaj warzywa na kremowymserku. Po wierzchu posyp całość posiekanym szczypiorkiem. Do przygotowania tych kanapek możesz użyć również innychświeżych warzyw np. ogórka, papryki lub rukolii. " },
