                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "zapiekanka makaronowo warzywna", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/zapiekanka-makaronowo-warzywna.jpg", Time = 60, Number_Person = 4, Steps = 5, Instruction = " Ugotuj makaron w osolonej wodzie, wyjmij, przelej zimną wodą i odcedź. 	 Posiekaj cebule i czosnek, zrumień na części margaryny. Dodaj do makaronu. 	Naczynie żaroodporne wysmaruj pozostałą margaryną i napełnij mieszanką makaronu, cebuli i czosnku.	Pozostałe warzywa obierz i opłucz, a następnie pokrój marchew w plasterki, pora w 3 centymetrowe kawałki. Razem z brukselką ugotuj je na parze, dodaj do makaronu i wymieszaj.	Mleko wymieszaj z Fixem Knorr, zagotuj i po lekkim schłodzeniu dodaj do niego rozkłócone jajko i natkę pietruszki. Wymieszaj.	Polej potrawę i posyp żółtym serem. Zapiekankę makaronowo – warzywną piecz 25 minut w średnio nagrzanym piekarniku. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "palki z kurczaka w sliwkowej marynacie", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/palki-z-kurczaka-w-sliwkowej-marynacie358101.jpg", Time = 60, Number_Person = 8, Steps = 2, Instruction = " Umyj i osusz pałki z kurczaka. W misce pomieszaj oliwę, ocet ryżowy, sos sojowy, przyprawę Knorr, powidła śliwkowe, starty imbir, posiekaną kolendrę oraz drobno pokrojoną papryczkę chilli. 	 W tak przygotowanej marynacie umieść pałki z kurczaka na około 12 godzin. Całość umieść w chłodnym miejscu.	Po tym czasie kurczaka grilluj na średnio rozgrzanym grillu na aluminiowych tackach, pod przykryciem przez około 40 minut. Udka cały czas przekręcaj i smaruj je marynatą co jakiś czas. Dobrym pomysłem jest ponacinacie udek, tak aby marynata szybciej weszła w głąb mięsa. Skróci to również czas grillowania." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka jajeczna", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-jajeczna.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Selera obierz i umyj. Następnie pokrój go na bardzo cienkie plastry i skrop cytryną. 	 Jajka ugotuj na twardo, po czym schłodź, obierz i pokrój na cząstki.	Szynkę pokrój w paski, sałatę umyj i odsącz z nadmiaru wody.	Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem oraz startym serem. Na koniec posyp sałatkę grzankami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "sos grzybowy z boczniaka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/sos-grzybowy-z-boczniaka-2.jpg", Time = 15, Number_Person = 5, Steps = 1, Instruction = " W garnku lub na patelni, na której przygotowywane było mięso, na rozgrzanym tłuszczu podsmaż posiekaną w drobną kostkę cebulę i pokrojone w plastry grzyby. 	 Dodaj 200 ml zimnej wody. Patelnię zestaw z ognia, dodaj esencję, dzięki której sos będzie gęsty i dobrze doprawiony. Całość dokładnie wymieszaj, upewniając się, że esencja zupełnie się rozpuściła. Wszystko razem gotuj dwie minuty, po czym sos zapraw śmietaną. Gotowy sos zmiksuj do uzyskania gładkiej konsystencji. Do sosu dodaj kilka kropel sosu sojowego. Wzmocni to jego smak i nada mu delikatnie orientalnego charakteru." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kotlety z jajek", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/7jpg.jpg", Time = 15, Number_Person = 1, Steps = 6, Instruction = " 8 jajek gotujemy na twardo. 2 sztuki odkładamy.	 Cebulę kroimy w kostkę. Szklimy na tłuszczu. 	Bułkę moczymy w wodzie.	Jajka obieramy ze skorupek ścieramy na tarce na grubych oczkach. 	Surowe jajka roztrzepujemy za pomocą widelca. Łączymy jajka z bułką, cebuląi pietruszką. 	Doprawiamy do smaku solą i pieprzem. Formujemy kotleciki. Obtaczamy w tartej bułce. 	Smażymy na tłuszczu na złoty kolor. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "jajka po benedyktynsku", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/05_eggs_benedict_hellmanns63255.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " W miseczce połącz i wymieszaj majonez Hellmann's, jogurt, mleko oraz przyprawę Knorr.Wstaw do kuchenki mikrofalowej na 30 sekund używając maksymalnej mocy. Sos wymieszaj trzepaczką na gładko.Kajzerki przekrój na pół i opiecz w piekarniku lub na patelni.Jajka ugotuj na miękko. Boczek usmaż na patelni na chrupko.	Na opieczonych połówkach bułki ułóż plastry boczku a następnie połóż ugotowane jajka. Wszystkie porcje polej przygotowanym gorącym sosem. Podawaj natychmiast po przygotowaniu. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "rozki z szynki z salatka jajeczna pod delikatna galaretka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/img_3830783037.jpg", Time = 30, Number_Person = 6, Steps = 2, Instruction = " W garnku zagotuj pół litra wody dodaj kostkę rosołową i żelatynę (około 10g). Całość mieszaj dokładnie aż żelatyna się rozpuści (uwaga nie gotuj ponownie). Wywar na koniec przecedź przez drobne sito odstaw do ostygnięcia. 	 Jaja zetrzyj na tarce i połącz z majonezem,odciśniętym chrzanem, pokrojonymi w kostkę ogórkami, groszkiem konserwowym oraz posiekanym szczypiorkiem i pokrojonym w drobną kosteczkę porem. Całość dokładnie wymieszaj – opcjonalnie dopraw do smaku szczyptą soli i pieprzu.	Z szynki wywiń równy rożek i umieść w nim sałatkę. Szynkę ułóż na liściu cykorii i połóż na talerzu. Całość wstaw do lodówki aby się schłodziła. Na koniec zalej delikatnie ścinającą się galaretą. Całość włóż ponownie do lodówki. Rożki z sałatką smakują wyśmienicie podawane z sosem chrzanowym. W tym celu wymieszaj dwie trzy łyżki majonezu z łyżką chrzanu. Jeśli chcesz, aby Twoje rożki wyglądały jeszcze ciekawiej, obwiąż każdy z nich szczypiorkiem. Pamiętaj jednak aby szczypiorek sparzyć wcześniej krótko we wrzącej wodzie. Zamiast szynki gotowanej lub konserwowej możesz użyć szynki suszonej, np. Schwarzwald i z niej przygotować roladki. Równie apatyczne będą roladki przygotowane z wędzonego łososia." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 2, Name_Recipe = "truskawkowy tort", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/truskawkowy-tort.jpg", Time = 60, Number_Person = 1, Steps = 8, Instruction = " Żółtka utrzeć z cukrem i dwoma rodzajami mąki. 	 Zalać gorącym mlekiem i wymieszać.	Postawić na parze i ubijać mikserem ok. 15 minut.	Odstawić do ostygnięcia.	Masło i Kasię utrzeć na puszysty krem.	Mieszając, dodawać po łyżeczce masy żółtkowej i połowę porcji rozgniecionych truskawek.	Resztę owoców (kilka odłożyć do dekoracji) dodać w całości.	Na ułożone w tortownicy biszkopty wyłożyć masę.	Nakryć drugą warstwą biszkoptów, pokryć masą, ozdobić całymi lub pokrojonymi w plasterki truskawkami, przyozdobić miętą lub i zalać galaretką. " },
