                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "curry z kurczaka", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/curry-z-kurczaka.jpg", Time = 45, Number_Person = 4, Steps = 2, Instruction = " Kurczaka pokrój w paski i obficie posyp curry oraz Przyprawą do złotego kurczaka Knorr. Pozostaw go na 20 minut w lodówce.	 Pokrój cebule i chilli, po czym podsmaż je. Dodaj kurczaka i ponownie podsmaż.	Następnie dodaj mleko kokosowe, śmietanę, pastę curry i sok z połówki limonki. Gotuj do momentu, gdy sos się zredukuje i odrobinę zgęstnieje. Podawaj z ryżem. " },
