                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "nadziewane skorupki ziemniakow potatoe skins", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/nadziewane-skorupki-ziemniakow.jpg", Time = 60, Number_Person = 4, Steps = 4, Instruction = " Wyszoruj ziemniaki i ugotuj je w mundurkach. Boczek, cebule i czosnek pokrój w kostkę i podsmaż. 	 Pieczarki pokrój drobno, dodaj do boczku i smaż całość 10 minut. 	Lekko wystudzone ziemniaki przekrój na połówki i używając łyżeczki wydłub środek. Skorupka ma mieć około 1 centymetra grubości. 	W misce wymieszaj resztki po drążeniu ziemniaków, pieczarki z boczkiem i całość dopraw kostkami Knorr.	Napełnij skorupki obficie farszem i posyp serem. Ułóż na blasze i zapiekaj w 190 stopniach dopóki ser nie zacznie się zarumieniać. Podawaj z gęstą śmietaną doprawioną posiekanym szczypiorkiem " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "ziolowe spaghetti bolognese z soczewica", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/knorr2014319717.jpg", Time = 30, Number_Person = 4, Steps = 3, Instruction = " Seler obierz z włókien i pokrój na nieduże kawałki. Cebulę przesmaż na rozgrzanej patelni, następnie dodaj kawałki selera. 	 Na patelnię dodaj pomidory, 100 ml wody oraz produkt Knorr Naturalnie smaczne - Spaghetti Bolognese, aby sos nabrał ziołowego smaku i idealnej konsystencji. Sos doprowadź do wrzenia.	Do sosu dodaj soczewicę, zmniejsz ogień i gotuj 10 minut.	Makaron ugotuj al dente w lekko osolonej wodzie, następnie odcedź i gorący wyłóż na talerze. Wierzch makaronu pokryj przygotowanym sosem i udekoruj listkami bazylii. Wierzch makaronu posyp obficie startym parmezanem. Jeśli nie masz parmezanu użyj zwykłego żółtego sera." },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z feta i awokado", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-feta-i-awokado.jpg", Time = 15, Number_Person = 4, Steps = 3, Instruction = " Przygotuj sos sałatkowy. Wymieszaj zawartość opakowania Knorr z majonezem i ketchupem.	 Awokado obierz, wyjmij pestkę i pokrój w kostkę. Pomarańcze obierz ze skóry, białej otuliny i wytnij z nich błony. Ser feta pokrój w kostkę. Umieść produkty w misce z dresingiem i dodaj sałatę.	Boczek pokrój w kostkę i podsmaż na patelni. Usuń wytopiony tłuszcz, boczek dodaj do pozostałych składników.	Całość dokładnie wymieszaj, nałóż do głębokich talerzy i udekoruj grzankami. " },
                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "salatka z selera i ananasa", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/salatka-z-selera-i-ananasa.jpg", Time = 15, Number_Person = 4, Steps = 5, Instruction = " Selera oczyść, obierz wzdłuż, pozbywając się włókien. Sparz w osolonym wrzątku, przełóż do miski z zimną wodą. Po chwili pokrój pod kątem w długie paski. 	 Rukolę umyj, wysusz i oczyść, dodaj do selera.	Orzechy upraż na patelni. Pomidorki pokrój na ćwiartki, a ananasa w kostkę. 	Pomarańczę obierz, wytnij nad miską filety, zachowując sok, który będzie ściekał do miski. 	Wymieszaj wszystkie warzywa i owoce. Dodaj rukolę oraz orzechy.	Sos sałatkowy Knorr wymieszaj ze świeżym sokiem pomarańczowym i oliwą z oliwek, następnie polej nim sałatkę. Sałatkę podawaj z grzankami. " },
