                new Recipe() { Id_Recipe = 1, Id_User = 1, Id_Category_Main = 1, Name_Recipe = "kanapki z wedzonym lososiem i awokado", Rate = 0, Level = "Łatwe", Date_Recipe = DateTime.Now, URL_Photo = "https://s3.przepisy.pl/przepisy3ii/img/variants/1280x0/kanapki-z-wedzonym-lososiem-i-awokado.jpg", Time = 15, Number_Person = 4, Steps = 2, Instruction = " Kromki pieczywa posmaruj majonezem a następnie każdą z nich przekrój po przekątnej na dwie połówki. Awokado obierz i pokrój w cienkie plastry. 	 Na każdej kromce połóż po plasterku łososia oraz awokado.	Wierzch kanapek udekoruj odrobiną majonezu, połóż krążki cebuli oraz po gałązce koperku. Aby urozmaicić takie kanapki możesz udekorować je odrobiną francuskiej musztardy." },
